import{_ as e,o as t,c as o,a as r}from"./app.4ab4139b.js";const b=JSON.parse('{"title":"Accessibility (a11y)","description":"","frontmatter":{},"headers":[{"level":2,"title":"# Integrate accessible UI controls","slug":"integrate-accessible-ui-controls","link":"#integrate-accessible-ui-controls","children":[{"level":3,"title":"# Standard controls","slug":"standard-controls","link":"#standard-controls","children":[]},{"level":3,"title":"# WAI-ARIA in custom controls","slug":"wai-aria-in-custom-controls","link":"#wai-aria-in-custom-controls","children":[]},{"level":3,"title":"# Focus in custom controls","slug":"focus-in-custom-controls","link":"#focus-in-custom-controls","children":[]}]},{"level":2,"title":"# Support keyboard access","slug":"support-keyboard-access","link":"#support-keyboard-access","children":[{"level":3,"title":"# Navigation","slug":"navigation","link":"#navigation","children":[]},{"level":3,"title":"# Shortcuts","slug":"shortcuts","link":"#shortcuts","children":[]}]},{"level":2,"title":"# Provide accessible content","slug":"provide-accessible-content","link":"#provide-accessible-content","children":[{"level":3,"title":"# Text","slug":"text","link":"#text","children":[]},{"level":3,"title":"# Colors","slug":"colors","link":"#colors","children":[]},{"level":3,"title":"# Sound","slug":"sound","link":"#sound","children":[]},{"level":3,"title":"# Images","slug":"images","link":"#images","children":[]}]},{"level":2,"title":"# Learn more","slug":"learn-more","link":"#learn-more","children":[]}],"relativePath":"chromedocs/Best practices/Accessibility (a11y) - Chrome Developers.md","lastUpdated":1679926139000}'),a={name:"chromedocs/Best practices/Accessibility (a11y) - Chrome Developers.md"},s=r(`<ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#controls" target="_blank" rel="noreferrer">Integrate accessible UI controls</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#htmlcontrols" target="_blank" rel="noreferrer">Standard controls</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#aria" target="_blank" rel="noreferrer">WAI-ARIA in custom controls</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#focus" target="_blank" rel="noreferrer">Focus in custom controls</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#keyboard" target="_blank" rel="noreferrer">Support keyboard access</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#navigation" target="_blank" rel="noreferrer">Navigation</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#shortcuts" target="_blank" rel="noreferrer">Shortcuts</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#more" target="_blank" rel="noreferrer">Provide accessible content</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#text" target="_blank" rel="noreferrer">Text</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#colors" target="_blank" rel="noreferrer">Colors</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#sound" target="_blank" rel="noreferrer">Sound</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#images" target="_blank" rel="noreferrer">Images</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#learn_more" target="_blank" rel="noreferrer">Learn more</a></li></ul><h1 id="accessibility-a11y" tabindex="-1">Accessibility (a11y) <a class="header-anchor" href="#accessibility-a11y" aria-hidden="true">#</a></h1><p>Published on Monday, September 17, 2012 \u2022 Updated on Monday, July 30, 2018</p><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#controls" target="_blank" rel="noreferrer">Integrate accessible UI controls</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#htmlcontrols" target="_blank" rel="noreferrer">Standard controls</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#aria" target="_blank" rel="noreferrer">WAI-ARIA in custom controls</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#focus" target="_blank" rel="noreferrer">Focus in custom controls</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#keyboard" target="_blank" rel="noreferrer">Support keyboard access</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#navigation" target="_blank" rel="noreferrer">Navigation</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#shortcuts" target="_blank" rel="noreferrer">Shortcuts</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#more" target="_blank" rel="noreferrer">Provide accessible content</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#text" target="_blank" rel="noreferrer">Text</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#colors" target="_blank" rel="noreferrer">Colors</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#sound" target="_blank" rel="noreferrer">Sound</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#images" target="_blank" rel="noreferrer">Images</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#learn_more" target="_blank" rel="noreferrer">Learn more</a></li></ul><p>Extensions empower users to create their ideal browsing experience, tailored to an individual&#39;s abilities and preferences. Extensions should include accessibility components that encourage an inclusive user base by enabling people with visual impairments, hearing loss, limited dexterity, and other disabilities to access the extension.</p><p>Everyone, not just users with special needs, can benefit from accessibility features. Vision impaired, low dexterity and power users all benefit from keyboard shortcuts. Captions and transcripts are essential to deaf users, but also help language learners.</p><p>People may interact with an extension in a variety of ways. Some users have a standard monitor, keyboard and mouse - or they may depend on a screen magnifier and possibly a <a href="https://webaim.org/techniques/screenreader/" target="_blank" rel="noreferrer">screen reader</a>. While it is impossible to predict what tools people will use to access an extension, there are steps any developer can take to make an extension as accessible as possible.</p><h2 id="integrate-accessible-ui-controls" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#controls" target="_blank" rel="noreferrer">#</a> Integrate accessible UI controls <a class="header-anchor" href="#integrate-accessible-ui-controls" aria-hidden="true">#</a></h2><p>If users can&#39;t access the user interface controls, they are unable to use an extension. The easiest way to create an accessible UI is to use a standard HTML control.</p><p>If an extension requires a custom control, it is much easier to make a custom control accessible from the beginning than to go back an add accessibility support later.</p><h3 id="standard-controls" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#htmlcontrols" target="_blank" rel="noreferrer">#</a> Standard controls <a class="header-anchor" href="#standard-controls" aria-hidden="true">#</a></h3><p>Whenever possible, use <a href="https://developer.mozilla.org/docs/Learn/Accessibility/HTML" target="_blank" rel="noreferrer">standard HTML UI controls</a>. Standard HTML controls are keyboard accessible, scale easily and are generally understood by screen readers.</p><p>![Screenshots and code for button, checkbox, radio, text, select/option, and link](./Accessibility (a11y) - Chrome Developers_files/GzBvxe7wf61STDT30M2C.png)</p><h3 id="wai-aria-in-custom-controls" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#aria" target="_blank" rel="noreferrer">#</a> WAI-ARIA in custom controls <a class="header-anchor" href="#wai-aria-in-custom-controls" aria-hidden="true">#</a></h3><p>The <a href="https://www.w3.org/WAI/standards-guidelines/aria/" target="_blank" rel="noreferrer">Web Accessibility Initiative - Accessible Rich Internet Applications</a>, WAI-ARIA, is a specification for making UI controls accessible to screen readers through a standard set of DOM attributes. These attributes provide information to the screen reader about the function and current state of controls on a web page.</p><p>To add WAI-ARIA support to custom controls, the DOM elements of an extension will need to be modified to include attributes Chrome uses to raise events during user interaction. Screen readers respond to these events and describe the function of the control. DOM attributes specified by WAI-ARIA are classified into <a href="https://www.w3.org/TR/wai-aria/#roles" target="_blank" rel="noreferrer"><em>roles</em></a>, <a href="https://www.w3.org/TR/wai-aria/#states_and_properties" target="_blank" rel="noreferrer"><em>states</em>, and <em>properties</em></a>.</p><pre><code>&lt;div role=&quot;toolbar&quot;&gt;
</code></pre><p>The <a href="https://www.w3.org/WAI/PF/aria/states_and_properties#aria-activedescendant" target="_blank" rel="noreferrer"><code>aria-activedescendant</code></a> property specifies which child of a toolbar receives focus when the toolbar receives focus. The code <code>tabindex=&quot;0&quot;</code> specifies that the toolbar receives focus in document order.</p><p>Consider the complete specification for an example toolbar below:</p><pre><code>&lt;div role=&quot;toolbar&quot; tabindex=&quot;0&quot; aria-activedescendant=&quot;button1&quot;&gt;  &lt;img src=&quot;buttoncut.png&quot; role=&quot;button&quot; alt=&quot;cut&quot; id=&quot;button1&quot;&gt;  &lt;img src=&quot;buttoncopy.png&quot; role=&quot;button&quot; alt=&quot;copy&quot; id=&quot;button2&quot;&gt;  &lt;img src=&quot;buttonpaste.png&quot; role=&quot;button&quot; alt=&quot;paste&quot; id=&quot;button3&quot;&gt;&lt;/div&gt;
</code></pre><p>Once WAI-ARIA roles, states, and properties are added to the DOM of a control, Google Chrome raises the appropriate events to the screen reader. Because WAI-ARIA support is still a work in progress, Google Chrome might not raise an event for every WAI-ARIA property, and screen readers might not recognize all of the events being raised.</p><p>For a quick tutorial on adding WAI-ARIA controls to custom controls, see <a href="https://www.w3.org/2010/Talks/www2010-dsr-diy-aria/" target="_blank" rel="noreferrer">Dave Raggett&#39;s presentation from WWW2010</a>.</p><h3 id="focus-in-custom-controls" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#focus" target="_blank" rel="noreferrer">#</a> Focus in custom controls <a class="header-anchor" href="#focus-in-custom-controls" aria-hidden="true">#</a></h3><p>Keyboard focus is essentials for users who navigate the web without a mouse. Make sure operation and navigation controls, such as buttons, list boxes and menu bars can receive keyboard focus.</p><p>By default, the only elements in the HTML DOM that can receive keyboard focus are anchors, buttons, and form controls. However, setting the HTML attribute <code>tabIndex</code> to <code>0</code> places DOM elements in the default tab sequence, enabling them to receive keyboard focus.</p><pre><code>element.tabIndex = 0

element.focus();
</code></pre><p>Setting <code>tabIndex = -1</code> removes the element from the tab sequence but still allows the element to receive keyboard focus programmatically.</p><h2 id="support-keyboard-access" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#keyboard" target="_blank" rel="noreferrer">#</a> Support keyboard access <a class="header-anchor" href="#support-keyboard-access" aria-hidden="true">#</a></h2><p>Extensions should be usable with just a keyboard, allowing users who can&#39;t use a mouse, and power users who simply don&#39;t, to access them.</p><h3 id="navigation" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#navigation" target="_blank" rel="noreferrer">#</a> Navigation <a class="header-anchor" href="#navigation" aria-hidden="true">#</a></h3><p>Check that a user can navigate between different parts of an extension without using the mouse. Check that any use of a <a href="https://developer.chrome.com/docs/extensions/mv3/user_interface#popup" target="_blank" rel="noreferrer">popup</a> is keyboard navigable. Use <a href="https://support.google.com/chrome/answer/157179?hl=en" target="_blank" rel="noreferrer">Chrome keyboard shortcuts</a> to determine if an extension is navigable.</p><p>Make sure it&#39;s easy to see which parts of the interface have keyboard focus. Usually a focus outline moves around the interface, however, if CSS is used too heavily the outline might be suppressed or the contrast reduced.</p><p>![A focus outline on a Search button](./Accessibility (a11y) - Chrome Developers_files/KLh65zbJuxjLzdmYxFrP.png) ![A focus outline on one of a series of links](./Accessibility (a11y) - Chrome Developers_files/nrhQ8wybIh28GxRFfmSl.png)</p><h3 id="shortcuts" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#shortcuts" target="_blank" rel="noreferrer">#</a> Shortcuts <a class="header-anchor" href="#shortcuts" aria-hidden="true">#</a></h3><p>While the most common keyboard navigation strategy involves using the Tab key to rotate focus through an extension&#39;s interface, it is not always the easiest or most efficient option.</p><p>A simple JavaScript keyboard handler could look like the following. Note how the WAI-ARIA property <code>aria-activedescendant</code> is updated in response to user input to reflect the current active toolbar button.</p><pre><code> function optionKeyEvent(event) {  var tb = event.target;  var buttonid;  ENTER_KEYCODE = 13;  RIGHT_KEYCODE = 39;  LEFT_KEYCODE = 37;  // Partial sample code for processing arrow keys.  if (event.type == &quot;keydown&quot;) {    // Implement circular keyboard navigation within the toolbar buttons    if (event.keyCode == ENTER_KEYCODE) {      ExecuteButtonAction(getCurrentButtonID());      // getCurrentButtonID defined elsewhere    } else if (event.keyCode == event.RIGHT_KEYCODE) {      // Change the active toolbar button to the one to the right (circular).      var buttonid = getNextButtonID();      // getNextButtonID defined elsewhere      tb.setAttribute(&quot;aria-activedescendant&quot;, buttonid);    } else if (event.keyCode == event.LEFT_KEYCODE) {      // Change the active toolbar button to the one to the left (circular).      var buttonid = getPrevButtonID();      // getPrevButtonID defined elsewhere      tb.setAttribute(&quot;aria-activedescendant&quot;, buttonid);    } else {      return true;    }    return false;  }}

&lt;div role=&quot;toolbar&quot; tabindex=&quot;0&quot; aria-activedescendant=&quot;button1&quot; id=&quot;tb1&quot;     onkeydown=&quot;return optionKeyEvent(event);&quot;     onkeypress=&quot;return optionKeyEvent(event);&quot;&gt;  &lt;img src=&quot;buttoncut&quot; role=&quot;button&quot; alt=&quot;cut&quot; id=&quot;button1&quot;&gt;  &lt;img src=&quot;buttoncopy&quot; role=&quot;button&quot; alt=&quot;copy&quot; id=&quot;button1&quot;&gt;  &lt;img src=&quot;buttonpaste&quot; role=&quot;button&quot; alt=&quot;paste&quot; id=&quot;button1&quot;&gt;&lt;/div&gt;
</code></pre><p>Extensions can create explicit keyboard shortcuts to important extension UI elements. To implement these shortcuts, connect keyboard event listeners to controls. Make users aware of the available shortcuts by providing them in the <a href="https://developer.chrome.com/docs/extensions/mv3/options/" target="_blank" rel="noreferrer">options page</a>.</p><h2 id="provide-accessible-content" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#more" target="_blank" rel="noreferrer">#</a> Provide accessible content <a class="header-anchor" href="#provide-accessible-content" aria-hidden="true">#</a></h2><p>Providing accessible content is important to all users. Many of the following guidelines may sound familiar, as they reflect good practices for all web content.</p><h3 id="text" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#text" target="_blank" rel="noreferrer">#</a> Text <a class="header-anchor" href="#text" aria-hidden="true">#</a></h3><p>Font choices and text size impact how readable an extension&#39;s content is. Users with sight issues may need to increase an extensions text size. If using keyboard shortcuts, make sure they do not interfere with the zoom shortcuts built into Chrome.</p><p>As an indicator of flexibility of an extension&#39;s UI, apply the <a href="https://www.w3.org/TR/2008/REC-WCAG20-20081211/#visual-audio-contrast-scale" target="_blank" rel="noreferrer">200% test</a>; if the text size or page zoom is increased 200%, is it still usable?</p><p>Avoid baking text into images. Users are unable to modify the size and screen readers are unable to interpret images. Instead, opt for styled web font, such as one of the fonts found in the <a href="https://developers.google.com/fonts/" target="_blank" rel="noreferrer">Google Font API</a>. Web fonts can scale to different sizes and can be accessed by people using screen readers.</p><h3 id="colors" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#colors" target="_blank" rel="noreferrer">#</a> Colors <a class="header-anchor" href="#colors" aria-hidden="true">#</a></h3><p>There should be sufficient contrast between background color and the text color in an extension. Use a <a href="https://snook.ca/technical/colour_contrast/colour.html" target="_blank" rel="noreferrer">contrast checking tool</a> to test if the background and foreground colors provide an appropriate contrast.</p><p>When evaluating contrast, verify that every part of the extension that relies on graphics to convey information is clearly visible. For specific images, tools such as <a href="http://www.color-blindness.com/coblis-color-blindness-simulator/" target="_blank" rel="noreferrer">Coblis\u2014Color Blindness Simulator</a> can be used to see what an image looks like in various forms of color deficiency.</p><p>Consider offering different color themes, or giving the user the ability to customize the color scheme, to create a better contrast.</p><h3 id="sound" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#sound" target="_blank" rel="noreferrer">#</a> Sound <a class="header-anchor" href="#sound" aria-hidden="true">#</a></h3><p>If an extension relies upon sound or video to convey information, ensure that captions or a transcript is available. See the <a href="https://dcmp.org/learn/213" target="_blank" rel="noreferrer">Described and Captioned Media Program guidelines</a> for more information on captions.</p><h3 id="images" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#images" target="_blank" rel="noreferrer">#</a> Images <a class="header-anchor" href="#images" aria-hidden="true">#</a></h3><p>Provide informative alt text for images.</p><pre><code>&lt;img src=&quot;img.jpg&quot; alt=&quot;The logo for the extension&quot;&gt;
</code></pre><p>Use the alt text to state the purpose of the image rather than a literal description of the contents of an image. Spacer images or purely decorative images should have a blank <code>&quot;&quot;</code> alt text or removed from the HTML entirely and placed in the CSS.</p><p>If the extension must use text in an image, include the image text in the alt text. A good resource to refer to is the <a href="https://webaim.org/techniques/alttext/" target="_blank" rel="noreferrer">WebAIM article on appropriate alt text</a>.</p><h2 id="learn-more" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/a11y/#learn_more" target="_blank" rel="noreferrer">#</a> Learn more <a class="header-anchor" href="#learn-more" aria-hidden="true">#</a></h2><p>Learn more about accessibility in Chrome by checking out the <a href="https://www.youtube.com/watch?v=Ag3DMNbL_ig&amp;list=PLNYkxOF6rcICWx0C9LVWWVqvHlYJyqw7g" target="_blank" rel="noreferrer">A11ycasts</a> channel and reading through the <a href="https://www.chromium.org/developers/design-documents/accessibility#TOC-WAI-ARIA-Support" target="_blank" rel="noreferrer">Chromium Accessibility Technical Documentation</a>.</p><p>Updated on Monday, July 30, 2018 \u2022 <a href="https://github.com/GoogleChrome/developer.chrome.com/blob/main/site/en/docs/extensions/mv3/a11y/index.md" target="_blank" rel="noreferrer">Improve article</a></p>`,58),n=[s];function i(l,c,d,h,u,p){return t(),o("div",null,n)}const f=e(a,[["render",i]]);export{b as __pageData,f as default};
