import{_ as e,o as t,c as r,a as s}from"./app.f245760f.js";const f=JSON.parse('{"title":"Stay secure","description":"","frontmatter":{},"headers":[{"level":2,"title":"# Protect developer accounts","slug":"protect-developer-accounts","link":"#protect-developer-accounts","children":[{"level":3,"title":"# Keep groups selective","slug":"keep-groups-selective","link":"#keep-groups-selective","children":[]}]},{"level":2,"title":"# Never use HTTP, Ever","slug":"never-use-http-ever","link":"#never-use-http-ever","children":[]},{"level":2,"title":"# Request minimal permissions","slug":"request-minimal-permissions","link":"#request-minimal-permissions","children":[{"level":3,"title":"# Cross-origin XMLHttpRequest","slug":"cross-origin-xmlhttprequest","link":"#cross-origin-xmlhttprequest","children":[]}]},{"level":2,"title":"# Limit manifest fields","slug":"limit-manifest-fields","link":"#limit-manifest-fields","children":[{"level":3,"title":"# Externally connectable","slug":"externally-connectable","link":"#externally-connectable","children":[]},{"level":3,"title":"# Web-accessible resources","slug":"web-accessible-resources","link":"#web-accessible-resources","children":[]}]},{"level":2,"title":"# Include an explicit content security policy","slug":"include-an-explicit-content-security-policy","link":"#include-an-explicit-content-security-policy","children":[]},{"level":2,"title":"# Avoid executable APIs","slug":"avoid-executable-apis","link":"#avoid-executable-apis","children":[{"level":3,"title":"# document.write() and innerHTML","slug":"document-write-and-innerhtml","link":"#document-write-and-innerhtml","children":[]},{"level":3,"title":"# eval()","slug":"eval","link":"#eval","children":[]}]},{"level":2,"title":"# Use content scripts carefully","slug":"use-content-scripts-carefully","link":"#use-content-scripts-carefully","children":[]},{"level":2,"title":"# Register and sanitize inputs","slug":"register-and-sanitize-inputs","link":"#register-and-sanitize-inputs","children":[]}],"relativePath":"chromedocs/Best practices/Stay secure - Chrome Developers.md"}'),o={name:"chromedocs/Best practices/Stay secure - Chrome Developers.md"},n=s(`<ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#PDA" target="_blank" rel="noreferrer">Protect developer accounts</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#group_publishing" target="_blank" rel="noreferrer">Keep groups selective</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#https" target="_blank" rel="noreferrer">Never use HTTP, Ever</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#permissions" target="_blank" rel="noreferrer">Request minimal permissions</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#xhr" target="_blank" rel="noreferrer">Cross-origin XMLHttpRequest</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#manifest_fields" target="_blank" rel="noreferrer">Limit manifest fields</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#externally_connectable" target="_blank" rel="noreferrer">Externally connectable</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#web_accessible_resources" target="_blank" rel="noreferrer">Web-accessible resources</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#content_security_policy" target="_blank" rel="noreferrer">Include an explicit content security policy</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#avoid" target="_blank" rel="noreferrer">Avoid executable APIs</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#document_write" target="_blank" rel="noreferrer">document.write() and innerHTML</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#eval" target="_blank" rel="noreferrer">eval()</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#content_scripts" target="_blank" rel="noreferrer">Use content scripts carefully</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#sanitize" target="_blank" rel="noreferrer">Register and sanitize inputs</a></li></ul><h1 id="stay-secure" tabindex="-1">Stay secure <a class="header-anchor" href="#stay-secure" aria-hidden="true">#</a></h1><p>Published on Tuesday, March 6, 2018 \u2022 Updated on Wednesday, July 17, 2019</p><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#PDA" target="_blank" rel="noreferrer">Protect developer accounts</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#group_publishing" target="_blank" rel="noreferrer">Keep groups selective</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#https" target="_blank" rel="noreferrer">Never use HTTP, Ever</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#permissions" target="_blank" rel="noreferrer">Request minimal permissions</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#xhr" target="_blank" rel="noreferrer">Cross-origin XMLHttpRequest</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#manifest_fields" target="_blank" rel="noreferrer">Limit manifest fields</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#externally_connectable" target="_blank" rel="noreferrer">Externally connectable</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#web_accessible_resources" target="_blank" rel="noreferrer">Web-accessible resources</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#content_security_policy" target="_blank" rel="noreferrer">Include an explicit content security policy</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#avoid" target="_blank" rel="noreferrer">Avoid executable APIs</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#document_write" target="_blank" rel="noreferrer">document.write() and innerHTML</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#eval" target="_blank" rel="noreferrer">eval()</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#content_scripts" target="_blank" rel="noreferrer">Use content scripts carefully</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/security/#sanitize" target="_blank" rel="noreferrer">Register and sanitize inputs</a></li></ul><p>Extensions have access to special privileges within the browser, making them an appealing target for attackers. If an extension is compromised, <em>every</em> user of that extension becomes vulnerable to malicious and unwanted intrusion. Keep an extension secure and its users protected by incorporating these practices.</p><h2 id="protect-developer-accounts" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#PDA" target="_blank" rel="noreferrer">#</a> Protect developer accounts <a class="header-anchor" href="#protect-developer-accounts" aria-hidden="true">#</a></h2><p>Extension code is uploaded and updated through Google accounts. If developers&#39; accounts are compromised, an attacker could push malicious code directly to all users. Protect these accounts by creating specifically developer accounts and enabling <a href="https://support.google.com/accounts/answer/185839?hl=en" target="_blank" rel="noreferrer">two-factor authentication</a> , preferably with a <a href="https://support.google.com/accounts/answer/6103523" target="_blank" rel="noreferrer">security key</a> .</p><h3 id="keep-groups-selective" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#group_publishing" target="_blank" rel="noreferrer">#</a> Keep groups selective <a class="header-anchor" href="#keep-groups-selective" aria-hidden="true">#</a></h3><p>If using <a href="https://developer.chrome.com/docs/webstore/group-publishers/" target="_blank" rel="noreferrer">group publishing</a>, keep the group confined to trusted developers. Do not accept membership requests from unknown persons.</p><h2 id="never-use-http-ever" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#https" target="_blank" rel="noreferrer">#</a> Never use HTTP, Ever <a class="header-anchor" href="#never-use-http-ever" aria-hidden="true">#</a></h2><p>When requesting or sending data, avoid an HTTP connection. Assume that any HTTP connections will have eavesdroppers or contain modifications. HTTPS should always be preferred, as it has built-in security circumventing most <a href="https://www.owasp.org/index.php/Man-in-the-middle_attack" target="_blank" rel="noreferrer">man-in-the-middle attacks</a>.</p><h2 id="request-minimal-permissions" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#permissions" target="_blank" rel="noreferrer">#</a> Request minimal permissions <a class="header-anchor" href="#request-minimal-permissions" aria-hidden="true">#</a></h2><p>The Chrome browser limits an extension&#39;s access to privileges that have been explicitly requested in the <a href="https://developer.chrome.com/docs/extensions/mv3/manifest/" target="_blank" rel="noreferrer">manifest</a>. Extensions should minimize their permissions by only registering APIs and websites they depend on. Arbitrary code should be kept to a minimum.</p><p>Limiting an extensions privileges limits what a potential attacker can exploit.</p><h3 id="cross-origin-xmlhttprequest" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#xhr" target="_blank" rel="noreferrer">#</a> Cross-origin XMLHttpRequest <a class="header-anchor" href="#cross-origin-xmlhttprequest" aria-hidden="true">#</a></h3><p>An extension can only use <a href="https://developer.chrome.com/docs/extensions/mv3/xhr/" target="_blank" rel="noreferrer">XMLHttpRequest</a> to get resources from itself and from domains specified in the permissions.</p><pre><code>{  &quot;name&quot;: &quot;Very Secure Extension&quot;,  &quot;version&quot;: &quot;1.0&quot;,  &quot;description&quot;: &quot;Example of a Secure Extension&quot;,  &quot;permissions&quot;: [    &quot;https://developer.chrome.com/*&quot;,    &quot;https://*.google.com/*&quot;  ],  &quot;manifest_version&quot;: 2}
</code></pre><p>This extension requests access to anything on <a href="http://developer.chrome.com" target="_blank" rel="noreferrer">developer.chrome.com</a> and subdomains of Google by listing <code>&quot;https://developer.chrome.com/*&quot;</code> and <code>&quot;https://*.google.com/*&quot;</code> in the permissions. If the extension were compromised, it would still only have permission to interact with websites that meet the <a href="https://developer.chrome.com/docs/extensions/mv3/match_patterns/" target="_blank" rel="noreferrer">match pattern</a>. The attacker would not be able to access <code>&quot;https://user_bank_info.com&quot;</code> or interact with <code>&quot;https://malicious_website.com&quot;</code>.</p><h2 id="limit-manifest-fields" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#manifest_fields" target="_blank" rel="noreferrer">#</a> Limit manifest fields <a class="header-anchor" href="#limit-manifest-fields" aria-hidden="true">#</a></h2><p>Including unnecessary registrations in the manifest creates vulnerabilities and makes an extension more visible. Limit manifest fields to those the extension relies on and give specific field registration.</p><h3 id="externally-connectable" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#externally_connectable" target="_blank" rel="noreferrer">#</a> Externally connectable <a class="header-anchor" href="#externally-connectable" aria-hidden="true">#</a></h3><p>Use the <a href="https://developer.chrome.com/docs/extensions/mv3/manifest/externally_connectable/" target="_blank" rel="noreferrer"><code>externally_connectable</code></a> field to declare which external extensions and web pages the extension will exchange information with. Restrict who the extension can externally connect with to trusted sources.</p><pre><code>{  &quot;name&quot;: &quot;Super Safe Extension&quot;,  &quot;externally_connectable&quot;: {    &quot;ids&quot;: [      &quot;iamafriendlyextensionhereisdatas&quot;    ],    &quot;matches&quot;: [      &quot;https://developer.chrome.com/*&quot;,      &quot;https://*.google.com/*&quot;    ],    &quot;accepts_tls_channel_id&quot;: false  },  ...}
</code></pre><h3 id="web-accessible-resources" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#web_accessible_resources" target="_blank" rel="noreferrer">#</a> Web-accessible resources <a class="header-anchor" href="#web-accessible-resources" aria-hidden="true">#</a></h3><p>Making resources accessible by the web, under the <a href="https://developer.chrome.com/docs/extensions/mv3/manifest/web_accessible_resources/" target="_blank" rel="noreferrer"><code>web_accessible_resources</code></a> will make an extension detectable by websites and attackers.</p><pre><code>{  ...  &quot;web_accessible_resources&quot;: [    &quot;images/*.png&quot;,    &quot;style/secure_extension.css&quot;,    &quot;script/secure_extension.js&quot;  ],  ...}
</code></pre><p>The more web accessible resources available, the more avenues a potential attacker can exploit. Keep these files to a minimum.</p><h2 id="include-an-explicit-content-security-policy" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#content_security_policy" target="_blank" rel="noreferrer">#</a> Include an explicit content security policy <a class="header-anchor" href="#include-an-explicit-content-security-policy" aria-hidden="true">#</a></h2><p>Include a <a href="https://developer.chrome.com/docs/apps/contentSecurityPolicy/" target="_blank" rel="noreferrer">content security policy</a> for the extension in the manifest to prevent cross-site scripting attacks. If the extension only loads resources from itself register the following:</p><pre><code>{  &quot;name&quot;: &quot;Very Secure Extension&quot;,  &quot;version&quot;: &quot;1.0&quot;,  &quot;description&quot;: &quot;Example of a Secure Extension&quot;,  &quot;content_security_policy&quot;: &quot;default-src &#39;self&#39;; frame-ancestors &#39;none&#39;;&quot;,  &quot;manifest_version&quot;: 2}
</code></pre><p>If the extension needs to include scripts from specific hosts, they can be included:</p><pre><code>{  &quot;name&quot;: &quot;Very Secure Extension&quot;,  &quot;version&quot;: &quot;1.0&quot;,  &quot;description&quot;: &quot;Example of a Secure Extension&quot;,  &quot;content_security_policy&quot;: &quot;default-src &#39;self&#39; https://extension.resource.com; frame-ancestors &#39;none&#39;;&quot;,  &quot;manifest_version&quot;: 2}
</code></pre><h2 id="avoid-executable-apis" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#avoid" target="_blank" rel="noreferrer">#</a> Avoid executable APIs <a class="header-anchor" href="#avoid-executable-apis" aria-hidden="true">#</a></h2><p>APIs that execute code should be replaced with safer alternatives.</p><h3 id="document-write-and-innerhtml" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#document_write" target="_blank" rel="noreferrer">#</a> document.write() and innerHTML <a class="header-anchor" href="#document-write-and-innerhtml" aria-hidden="true">#</a></h3><p>While it may be simpler to dynamically create HTML elements with <code>document.write()</code> and <code>innerHTML</code>, it leaves the extension, and web pages the extension depends on, open to attackers inserting malicious scripts. Instead, manually create DOM nodes and use <code>innerText</code> to insert dynamic content.</p><pre><code>function constructDOM() {  let newTitle = document.createElement(&#39;h1&#39;);  newTitle.innerText = host;  document.appendChild(newTitle);}
</code></pre><h3 id="eval" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#eval" target="_blank" rel="noreferrer">#</a> eval() <a class="header-anchor" href="#eval" aria-hidden="true">#</a></h3><p>Avoid using <code>eval()</code> whenever possible to prevent attacks, as <code>eval()</code> will execute any code passed into it, which may be malicious.</p><pre><code>var xhr = new XMLHttpRequest();xhr.open(&quot;GET&quot;, &quot;https://api.example.com/data.json&quot;, true);xhr.onreadystatechange = function() {  if (xhr.readyState == 4) {    // WARNING! Might be evaluating an evil script!    var resp = eval(&quot;(&quot; + xhr.responseText + &quot;)&quot;);    ...  }}xhr.send();
</code></pre><p>Instead, prefer safer, and faster, methods such as <code>JSON.parse()</code></p><pre><code>var xhr = new XMLHttpRequest();xhr.open(&quot;GET&quot;, &quot;https://api.example.com/data.json&quot;, true);xhr.onreadystatechange = function() {  if (xhr.readyState == 4) {    // JSON.parse does not evaluate the attacker&#39;s scripts.    var resp = JSON.parse(xhr.responseText);  }}xhr.send();
</code></pre><h2 id="use-content-scripts-carefully" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#content_scripts" target="_blank" rel="noreferrer">#</a> Use content scripts carefully <a class="header-anchor" href="#use-content-scripts-carefully" aria-hidden="true">#</a></h2><p>While <a href="https://developer.chrome.com/docs/extensions/mv3/content_scripts/" target="_blank" rel="noreferrer">content scripts</a> live in an <a href="https://developer.chrome.com/docs/extensions/mv3/content_scripts#isolated_world" target="_blank" rel="noreferrer">isolated world</a>, they are not immune from attacks:</p><ul><li>Content scripts are the only part of an extension that interacts directly with the web page. Because of this, hostile web pages may manipulate parts of the DOM the content script depends on, or exploit surprising web standard behavior, such as <a href="https://html.spec.whatwg.org/#dom-window-nameditem" target="_blank" rel="noreferrer">named items</a>.</li><li>To interact with DOM of web pages, content scripts need to execute in the same renderer process as the web page. This makes content scripts vulnerable to leaking data via side channel attacks (e.g., <a href="https://spectreattack.com/" target="_blank" rel="noreferrer">Spectre</a>), and to being taken over by an attacker if a malicious web page compromises the renderer process.</li></ul><p>Sensitive work should be performed in a dedicated process, such as the extension&#39;s <a href="https://developer.chrome.com/docs/extensions/mv3/background_pages/" target="_blank" rel="noreferrer">background script</a>. Avoid accidentally exposing extension privileges to content scripts:</p><ul><li>Assume that <a href="https://developer.chrome.com/docs/extensions/mv3/messaging#content-scripts-are-less-trustworthy" target="_blank" rel="noreferrer">messages from a content script</a> might have been crafted by an attacker (e.g. <a href="https://developer.chrome.com/docs/extensions/mv3/security/#sanitize" target="_blank" rel="noreferrer">validate and sanitize</a> all input and protect your scripts from <a href="https://developer.chrome.com/docs/extensions/mv3/messaging#cross-site-scripting" target="_blank" rel="noreferrer">cross-site scripting</a>).</li><li>Assume any data sent to the content script might leak to the web page. Do not send sensitive data (e.g. secrets from the extension, data from other web origins, browsing history) to content scripts.</li><li>Limit the scope of privileged actions that can be triggered by content scripts. Do not allow content scripts to <a href="https://developer.chrome.com/docs/extensions/mv3/xhr#xhr-vs-content-scripts" target="_blank" rel="noreferrer">trigger requests to arbitrary URLs</a> or pass arbitrary arguments to extension APIs (e.g., do not allow passing arbitrary URLs to <a href="https://developer.mozilla.org/docs/Web/API/Fetch_API" target="_blank" rel="noreferrer"><code>fetch</code></a> or <a href="https://developer.chrome.com/docs/extensions/reference/tabs#method-create" target="_blank" rel="noreferrer"><code>chrome.tabs.create</code></a> API).</li></ul><h2 id="register-and-sanitize-inputs" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/security/#sanitize" target="_blank" rel="noreferrer">#</a> Register and sanitize inputs <a class="header-anchor" href="#register-and-sanitize-inputs" aria-hidden="true">#</a></h2><p>Safeguard an extension from malicious scripts by limiting listeners to only what the extension is expecting, validating the senders of incoming data, and sanitizing all inputs.</p><p>An extension should only register for <a href="https://developer.chrome.com/docs/extensions/reference/runtime#event-onMessageExternal" target="_blank" rel="noreferrer"><code>runtime.onRequestExternal</code></a>, if it is expecting communication from an external website or extension. Always validate that the sender matches a trusted source.</p><pre><code>// The ID of an external extensionconst kFriendlyExtensionId = &quot;iamafriendlyextensionhereisdatas&quot;;chrome.runtime.onMessageExternal.addListener(  function(request, sender, sendResponse) {    if (sender.id === kFriendlyExtensionId)      doSomething();});
</code></pre><p>Even messages via <a href="https://developer.chrome.com/docs/extensions/reference/runtime#event-onMessage" target="_blank" rel="noreferrer">runtime.onMessage</a> event from the extension itself should be scrutinized to ensure the <a href="https://developer.chrome.com/docs/extensions/reference/runtime#type-MessageSender" target="_blank" rel="noreferrer">MessageSender</a> is not from a compromised <a href="https://developer.chrome.com/docs/extensions/mv3/content_scripts/" target="_blank" rel="noreferrer">content script</a>.</p><pre><code>chrome.runtime.onMessage.addListener(function(request, sender, sendResponse) {  if (request.allowedAction)    console.log(&quot;This is an allowed action.&quot;);});
</code></pre><p>Prevent an extension from executing an attacker&#39;s script by sanitizing user inputs and incoming data, even from the extension itself and approved sources. <a href="https://developer.chrome.com/docs/extensions/mv3/security#avoid" target="_blank" rel="noreferrer">Avoid executable APIs</a>.</p><pre><code>function sanitizeInput(input) {    return input.replace(/&amp;/g, &#39;&amp;amp;&#39;).replace(/&lt;/g, &#39;&amp;lt;&#39;).replace(/&quot;/g, &#39;&amp;quot;&#39;);}
</code></pre><p>Updated on Wednesday, July 17, 2019 \u2022 <a href="https://github.com/GoogleChrome/developer.chrome.com/blob/main/site/en/docs/extensions/mv3/security/index.md" target="_blank" rel="noreferrer">Improve article</a></p>`,56),a=[n];function i(c,l,d,p,h,u){return t(),r("div",null,a)}const v=e(o,[["render",i]]);export{f as __pageData,v as default};
