import{_ as e,o as r,c as t,a as o}from"./app.8d96fb22.js";const v=JSON.parse('{"title":"Architecture overview","description":"","frontmatter":{},"headers":[{"level":2,"title":"# Architecture","slug":"architecture","link":"#architecture","children":[{"level":3,"title":"# The manifest","slug":"the-manifest","link":"#the-manifest","children":[]},{"level":3,"title":"# Toolbar icon","slug":"toolbar-icon","link":"#toolbar-icon","children":[]},{"level":3,"title":"# Service worker","slug":"service-worker","link":"#service-worker","children":[]},{"level":3,"title":"# Content scripts","slug":"content-scripts","link":"#content-scripts","children":[]},{"level":3,"title":"# UI elements","slug":"ui-elements","link":"#ui-elements","children":[]},{"level":3,"title":"# Options page","slug":"options-page","link":"#options-page","children":[]},{"level":3,"title":"# Additional HTML files","slug":"additional-html-files","link":"#additional-html-files","children":[]}]},{"level":2,"title":"# Extension files","slug":"extension-files","link":"#extension-files","children":[{"level":3,"title":"# Referencing extension files","slug":"referencing-extension-files","link":"#referencing-extension-files","children":[]},{"level":3,"title":"# Web-accesible resources","slug":"web-accesible-resources","link":"#web-accesible-resources","children":[]}]},{"level":2,"title":"# Using Chrome APIs","slug":"using-chrome-apis","link":"#using-chrome-apis","children":[{"level":3,"title":"# Asynchronous methods","slug":"asynchronous-methods","link":"#asynchronous-methods","children":[]}]},{"level":2,"title":"# Communication between pages","slug":"communication-between-pages","link":"#communication-between-pages","children":[]},{"level":2,"title":"# Saving data","slug":"saving-data","link":"#saving-data","children":[]},{"level":2,"title":"# Incognito mode","slug":"incognito-mode","link":"#incognito-mode","children":[]},{"level":2,"title":"# Take the next step","slug":"take-the-next-step","link":"#take-the-next-step","children":[]}],"relativePath":"chromedocs/Develop extensions and themes/Architecture overview - Chrome Developers.md","lastUpdated":1679926139000}'),n={name:"chromedocs/Develop extensions and themes/Architecture overview - Chrome Developers.md"},s=o(`<ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#arch" target="_blank" rel="noreferrer">Architecture</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#manifest" target="_blank" rel="noreferrer">The manifest</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#icons" target="_blank" rel="noreferrer">Toolbar icon</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#background_script" target="_blank" rel="noreferrer">Service worker</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#contentScripts" target="_blank" rel="noreferrer">Content scripts</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#pages" target="_blank" rel="noreferrer">UI elements</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#optionsPage" target="_blank" rel="noreferrer">Options page</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#html-files" target="_blank" rel="noreferrer">Additional HTML files</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#files" target="_blank" rel="noreferrer">Extension files</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#ref-files" target="_blank" rel="noreferrer">Referencing extension files</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#web-resources" target="_blank" rel="noreferrer">Web-accesible resources</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#apis" target="_blank" rel="noreferrer">Using Chrome APIs</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#async-sync" target="_blank" rel="noreferrer">Asynchronous methods</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#pageComm" target="_blank" rel="noreferrer">Communication between pages</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#data" target="_blank" rel="noreferrer">Saving data</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#incognito" target="_blank" rel="noreferrer">Incognito mode</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#next-steps" target="_blank" rel="noreferrer">Take the next step</a></li></ul><h1 id="architecture-overview" tabindex="-1">Architecture overview <a class="header-anchor" href="#architecture-overview" aria-hidden="true">#</a></h1><p>A high-level explanation of the components and structure of a Chrome Extension.</p><p>Published on Tuesday, September 18, 2012 \u2022 Updated on Wednesday, November 2, 2022</p><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#arch" target="_blank" rel="noreferrer">Architecture</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#manifest" target="_blank" rel="noreferrer">The manifest</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#icons" target="_blank" rel="noreferrer">Toolbar icon</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#background_script" target="_blank" rel="noreferrer">Service worker</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#contentScripts" target="_blank" rel="noreferrer">Content scripts</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#pages" target="_blank" rel="noreferrer">UI elements</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#optionsPage" target="_blank" rel="noreferrer">Options page</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#html-files" target="_blank" rel="noreferrer">Additional HTML files</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#files" target="_blank" rel="noreferrer">Extension files</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#ref-files" target="_blank" rel="noreferrer">Referencing extension files</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#web-resources" target="_blank" rel="noreferrer">Web-accesible resources</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#apis" target="_blank" rel="noreferrer">Using Chrome APIs</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#async-sync" target="_blank" rel="noreferrer">Asynchronous methods</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#pageComm" target="_blank" rel="noreferrer">Communication between pages</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#data" target="_blank" rel="noreferrer">Saving data</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#incognito" target="_blank" rel="noreferrer">Incognito mode</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#next-steps" target="_blank" rel="noreferrer">Take the next step</a></li></ul><p>Extensions are zipped bundles of HTML, CSS, JavaScript, images, and other files used in the web platform. Extensions can modify web content that users see and interact with. They can also extend and change the behavior of the browser itself.</p><p>This page briefly describes the files that could form part of an extension, how to access these files, how to use Chrome APIs, how extension files communicate, and how to store data.</p><h2 id="architecture" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#arch" target="_blank" rel="noreferrer">#</a> Architecture <a class="header-anchor" href="#architecture" aria-hidden="true">#</a></h2><p>An extension&#39;s architecture will depend on its functionality, but all extensions must have a <a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#manifest" target="_blank" rel="noreferrer">manifest</a>. The following are other components an extension can include:</p><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#background_script" target="_blank" rel="noreferrer">Service worker</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#icons" target="_blank" rel="noreferrer">Toolbar icon</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#pages" target="_blank" rel="noreferrer">UI elements</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#contentScripts" target="_blank" rel="noreferrer">Content script</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#optionsPage" target="_blank" rel="noreferrer">Options page</a></li></ul><h3 id="the-manifest" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#manifest" target="_blank" rel="noreferrer">#</a> The manifest <a class="header-anchor" href="#the-manifest" aria-hidden="true">#</a></h3><p>The manifest file, titled <code>manifest.json</code>, gives the browser information about the extension, such as the most important files and the capabilities the extension might use.</p><pre><code>{  &quot;name&quot;: &quot;My Extension&quot;,  &quot;description&quot;: &quot;A nice little demo extension.&quot;,  &quot;version&quot;: &quot;2.1&quot;,  &quot;manifest_version&quot;: 3,  &quot;icons&quot;: {    &quot;16&quot;: &quot;icon_16.png&quot;,    &quot;48&quot;: &quot;icon_48.png&quot;,    &quot;128&quot;: &quot;icon_128.png&quot;  },  &quot;background&quot;: {    &quot;service_worker&quot;: &quot;background.js&quot;  },  &quot;permissions&quot;: [&quot;storage&quot;],  &quot;host_permissions&quot;: [&quot;*://*.example.com/*&quot;],  &quot;action&quot;: {    &quot;default_icon&quot;: &quot;icon_16.png&quot;,    &quot;default_popup&quot;: &quot;popup.html&quot;  }}
</code></pre><h3 id="toolbar-icon" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#icons" target="_blank" rel="noreferrer">#</a> Toolbar icon <a class="header-anchor" href="#toolbar-icon" aria-hidden="true">#</a></h3><p>Extensions must have an icon that sits in the browser toolbar. Toolbar icons allow easy access and keep users aware of which extensions are installed. Most users will interact with an extension that uses a <a href="https://developer.chrome.com/docs/extensions/mv3/user_interface#popup" target="_blank" rel="noreferrer">popup</a> by clicking the icon, like in the <a href="https://github.com/GoogleChrome/chrome-extensions-samples/tree/main/tutorials/getting-started" target="_blank" rel="noreferrer">getting started example</a>.</p><p>![Getting started popup](./Architecture overview - Chrome Developers_files/ku5Z8MMssgw6MKctpJVI.png)</p><h3 id="service-worker" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#background_script" target="_blank" rel="noreferrer">#</a> Service worker <a class="header-anchor" href="#service-worker" aria-hidden="true">#</a></h3><p>The extension service worker is the extension&#39;s event handler; it contains listeners for browser events that are important to the extension. It lies dormant until an event is fired then performs the instructed logic; it is only loaded when it is needed and unloaded when it goes idle. The service worker has access to all the <a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#apis" target="_blank" rel="noreferrer">Chrome APIs</a>, as long as it declares the required permissions in the <code>manifest.json</code>.</p><p>An extension can only have a single service worker. To import further code, the service worker can be declared as an <a href="https://web.dev/es-modules-in-sw/#static-imports-only" target="_blank" rel="noreferrer">ES Module</a> by specifying <code>&quot;type&quot;: &quot;module&quot;</code> in the manifest <code>&quot;background&quot;</code>.</p><p>See <a href="https://developer.chrome.com/docs/extensions/mv3/service_workers/" target="_blank" rel="noreferrer">Manage events with service workers</a> to learn more.</p><h3 id="content-scripts" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#contentScripts" target="_blank" rel="noreferrer">#</a> Content scripts <a class="header-anchor" href="#content-scripts" aria-hidden="true">#</a></h3><p>Content scripts allow extensions to inject logic into a page in order to read and modify its contents. A content script contains JavaScript that executes in the context of a page that has been loaded into the browser.</p><p>Content scripts can communicate with their parent extension by exchanging <a href="https://developer.chrome.com/docs/extensions/mv3/messaging/" target="_blank" rel="noreferrer">messages</a> and storing values using the <a href="https://developer.chrome.com/docs/extensions/reference/storage/" target="_blank" rel="noreferrer">storage</a> API.</p><p>![Shows a communication path between the content script and the parent extension](./Architecture overview - Chrome Developers_files/466ftDp0EXB4E1XeaGh0.png)</p><p>See <a href="https://developer.chrome.com/docs/extensions/mv3/content_scripts/" target="_blank" rel="noreferrer">Understanding content scripts</a> to learn more.</p><h3 id="ui-elements" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#pages" target="_blank" rel="noreferrer">#</a> UI elements <a class="header-anchor" href="#ui-elements" aria-hidden="true">#</a></h3><p>An extension&#39;s user interface should be purposeful and minimal. The UI should customize or enhance the browsing experience without distracting from it.</p><p>The following is a list of the most common UI examples:</p><ul><li>An <a href="https://developer.chrome.com/docs/extensions/mv3/user_interface/#click-event" target="_blank" rel="noreferrer">action click</a> event.</li><li>A <a href="https://developer.chrome.com/docs/extensions/mv3/user_interface#popup" target="_blank" rel="noreferrer">popup</a>.</li><li>A <a href="https://developer.chrome.com/docs/extensions/mv3/user_interface/#context_menu" target="_blank" rel="noreferrer">context menu</a>.</li><li>An <a href="https://developer.chrome.com/docs/extensions/mv3/user_interface/#omnibox" target="_blank" rel="noreferrer">omnibox</a>.</li><li>A <a href="https://developer.chrome.com/docs/extensions/mv3/user_interface/#commands" target="_blank" rel="noreferrer">keyboard shortcut</a>.</li><li>Desktop <a href="https://developer.chrome.com/docs/extensions/reference/notifications/" target="_blank" rel="noreferrer">notifications</a>.</li><li><a href="https://developer.chrome.com/docs/extensions/reference/tts/" target="_blank" rel="noreferrer">Text-to-speech</a>.</li><li>A custom UI injected <a href="https://developer.chrome.com/docs/extensions/mv3/content_scripts/" target="_blank" rel="noreferrer">into a page</a>.</li></ul><p>See <a href="https://developer.chrome.com/docs/extensions/mv3/user_interface/" target="_blank" rel="noreferrer">Design the UI of a Chrome extension</a>, to learn more.</p><h3 id="options-page" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#optionsPage" target="_blank" rel="noreferrer">#</a> Options page <a class="header-anchor" href="#options-page" aria-hidden="true">#</a></h3><p>Just as extensions allow users to customize the Chrome browser, the options page enables customization of the extension. Options can be used to enable features and allow users to choose what functionality is relevant to their needs.</p><p>Users can access the options page via <a href="https://developer.chrome.com/docs/extensions/mv3/options/#linking" target="_blank" rel="noreferrer">direct link</a> or in the context menu of the extension toolbar. The following is an example of the Google Dictionary extension.</p><p>![Options page link in the UI](./Architecture overview - Chrome Developers_files/Mz7GV76tFkzxRlb7Pq6e.png)</p><p>Link to the Options page.</p><p>![Context Menu Options page](./Architecture overview - Chrome Developers_files/BM11QeGCThsUNTlsZbAe.png)</p><p>Options page in the extension&#39;s context menu.</p><p>See <a href="https://developer.chrome.com/docs/extensions/mv3/options/" target="_blank" rel="noreferrer">Give users options</a> to learn more.</p><h3 id="additional-html-files" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#html-files" target="_blank" rel="noreferrer">#</a> Additional HTML files <a class="header-anchor" href="#additional-html-files" aria-hidden="true">#</a></h3><p>An extension can also have other HTML files that are not declared in the manifest. All extension HTML files can access the <a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#apis" target="_blank" rel="noreferrer">Chrome APIs</a> and can use script tags including Javascript files, but cannot declare inline JavaScript.</p><p>You can open these pages using the web api <a href="https://developer.mozilla.org/docs/Web/API/Window/open" target="_blank" rel="noreferrer">window.open()</a>, the Chrome APIs <a href="https://developer.chrome.com/docs/extensions/reference/windows/#method-create" target="_blank" rel="noreferrer">windows.create()</a>, or <a href="https://developer.chrome.com/docs/extensions/reference/tabs#method-create" target="_blank" rel="noreferrer">tabs.create()</a>.</p><h2 id="extension-files" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#files" target="_blank" rel="noreferrer">#</a> Extension files <a class="header-anchor" href="#extension-files" aria-hidden="true">#</a></h2><h3 id="referencing-extension-files" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#ref-files" target="_blank" rel="noreferrer">#</a> Referencing extension files <a class="header-anchor" href="#referencing-extension-files" aria-hidden="true">#</a></h3><p>Just as HTML pages on the web can include files on the same site with <em>relative URLs</em>, <strong>extension pages</strong> can also reference extension assets using relative paths.</p><pre><code>&lt;img src=&quot;images/my_image.png&quot;&gt;
</code></pre><p>To access an extension file from a <strong>content script</strong>, you can call <a href="https://developer.chrome.com/docs/extensions/reference/runtime#method-getURL" target="_blank" rel="noreferrer"><code>chrome.runtime.getURL()</code></a> to get the <em>absolute URL</em> of your extension asset.</p><pre><code>let image = chrome.runtime.getURL(&quot;images/my_image.png&quot;)
</code></pre><p>To access an extension file from a <strong>website</strong>, you will have to construct the URL as follows:</p><pre><code>chrome-extension://EXTENSION_ID/RELATIVE_PATH
</code></pre><p>You can find the <code>EXTENSION_ID</code> in the Extension management page <strong>chrome://extensions</strong>. The <code>RELATIVE_PATH</code> is the file path relative to the extension&#39;s top folder.</p><p>Key Term</p><p>The <strong>extension ID</strong> is a 32-character alpha string that identifies an extension in the browser and on the Chrome Web Store.</p><p>During development, a new ID is generated when an <a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/#unpacked" target="_blank" rel="noreferrer"><em>unpacked extension</em></a> is loaded, unless the <code>&quot;key&quot;</code> property is <a href="https://developer.chrome.com/docs/extensions/mv3/tut_oauth/#keep-consistent-id" target="_blank" rel="noreferrer">set in the manifest</a>.</p><p>Caution</p><p>All assets that content scripts and websites want to access must be declared under <a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#web-resources" target="_blank" rel="noreferrer"><code>web_accessible_resources</code></a> key in the manifest.</p><h3 id="web-accesible-resources" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#web-resources" target="_blank" rel="noreferrer">#</a> Web-accesible resources <a class="header-anchor" href="#web-accesible-resources" aria-hidden="true">#</a></h3><p>Web-accessible resources are files (images, HTML, CSS, Javascript) inside an extension that can be accessed by a content script, web pages, or other extensions.</p><p>You can declare which resources are exposed and to what origins in the manifest:</p><pre><code>{  ...  &quot;web_accessible_resources&quot;: [    {      &quot;resources&quot;: [ &quot;images/*.png&quot; ],      &quot;matches&quot;: [ &quot;https://example.com/*&quot; ]    }  ],  ...}
</code></pre><p>See <a href="https://developer.chrome.com/docs/extensions/mv3/manifest/web_accessible_resources/" target="_blank" rel="noreferrer">Web-accesible resources</a> for usage information.</p><h2 id="using-chrome-apis" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#apis" target="_blank" rel="noreferrer">#</a> Using Chrome APIs <a class="header-anchor" href="#using-chrome-apis" aria-hidden="true">#</a></h2><p>In addition to having access to the same <a href="https://developer.mozilla.org/docs/Web/API" target="_blank" rel="noreferrer">web APIs</a> as web pages, extensions can also use <a href="https://developer.chrome.com/docs/extensions/reference/" target="_blank" rel="noreferrer">extension-specific APIs</a> that create tight integration with the browser. For example, both extensions and webpages can access the standard <a href="https://developer.mozilla.org/docs/Web/API/Window/open" target="_blank" rel="noreferrer"><code>window.open()</code></a> method to open a URL, but extensions can specify which window that URL should be displayed in by using <a href="https://developer.chrome.com/docs/extensions/reference/tabs#method-create" target="_blank" rel="noreferrer">chrome.tabs.create()</a> instead.</p><p>For more information, explore the <a href="https://developer.chrome.com/docs/extensions/reference/" target="_blank" rel="noreferrer">Chrome API reference docs</a>.</p><h3 id="asynchronous-methods" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#async-sync" target="_blank" rel="noreferrer">#</a> Asynchronous methods <a class="header-anchor" href="#asynchronous-methods" aria-hidden="true">#</a></h3><p>Most Chrome API methods are asynchronous; they return immediately without waiting for the operation to finish. If an extension needs to know the outcome of an asynchronous operation, there are two choices:</p><ul><li>Use the returned promise.</li><li>Pass a callback function into the method.</li></ul><p>Note that these choices are mutually exclusive. If you pass a callback to a method, no promise will be returned. If you use the returned promise, do not pass a callback.</p><p>Generally, you should prefer promises to callbacks. Not all methods in extensions APIs support promises, but newer methods do. You can verify whether a method supports promises by checking its API reference page. If you need to support both promises and callbacks for the same function (because your users have older browsers), you can test whether the method returns a promise using <code>typeof</code> and <a href="https://developer.mozilla.org/docs/Web/JavaScript/Reference/Operators/Optional_chaining" target="_blank" rel="noreferrer">optional chaining</a>. For example:</p><pre><code>typeof chrome.contextMenus.removeAll()?.then()
</code></pre><h4 id="promises" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#async" target="_blank" rel="noreferrer">#</a> Promises <a class="header-anchor" href="#promises" aria-hidden="true">#</a></h4><p>Both methods of handling promises are supported. See <a href="https://developer.chrome.com/docs/extensions/mv3/promises/" target="_blank" rel="noreferrer">Using promises</a> to learn more.</p><pre><code>// Promisechrome.tabs.query(queryOptions).then((tabs) =&gt; {  chrome.tabs.update(tabs[0].id, {url: newUrl});  someOtherFunction();});// async-awaitasync function queryTab() {  let tabs = await chrome.tabs.query(queryOptions);  chrome.tabs.update(tabs[0].id, {url: newUrl});  someOtherFunction();}
</code></pre><h4 id="callbacks" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#callbacks" target="_blank" rel="noreferrer">#</a> Callbacks <a class="header-anchor" href="#callbacks" aria-hidden="true">#</a></h4><p>A method is asynchronous when the callback parameter is available in its signature.</p><pre><code>// Signatures for an asynchronous methodchrome.tabs.query(object queryInfo, function callback)
</code></pre><h2 id="communication-between-pages" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#pageComm" target="_blank" rel="noreferrer">#</a> Communication between pages <a class="header-anchor" href="#communication-between-pages" aria-hidden="true">#</a></h2><p>Different components in an extension can communicate with each other using <a href="https://developer.chrome.com/docs/extensions/mv3/messaging/" target="_blank" rel="noreferrer">message passing</a>. Either side can listen for messages sent from the other end, and respond on the same channel.</p><h2 id="saving-data" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#data" target="_blank" rel="noreferrer">#</a> Saving data <a class="header-anchor" href="#saving-data" aria-hidden="true">#</a></h2><p>The chrome storage API has been optimized to meet the specific storage needs of extensions. For example, whenever data is updated, you can use the <code>onChanged()</code> event to track these changes. All extension components have access to this API. An extension can also store data using the web API <a href="https://developer.mozilla.org/docs/Web/API/IndexedDB_API" target="_blank" rel="noreferrer">indexedDB</a>.</p><p>See <a href="https://developer.chrome.com/docs/extensions/reference/storage/" target="_blank" rel="noreferrer">storage API</a> for usage and examples.</p><h2 id="incognito-mode" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#incognito" target="_blank" rel="noreferrer">#</a> Incognito mode <a class="header-anchor" href="#incognito-mode" aria-hidden="true">#</a></h2><p>Extensions don&#39;t run in incognito windows unless the user manually allows it in the extension&#39;s settings page. By default, normal and incognito windows run in a single shared process. However, extensions can run incognito windows in their own separate process or not support incognito windows at all. You can specify this behavior in the <a href="https://developer.chrome.com/docs/extensions/mv3/manifest/incognito/" target="_blank" rel="noreferrer">&quot;incognito&quot;</a> key in the manifest.</p><p>See <a href="https://developer.chrome.com/docs/extensions/mv3/user_privacy/#data-incognito" target="_blank" rel="noreferrer">Saving data in incognito mode</a> to understand how to protect user privacy.</p><h2 id="take-the-next-step" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#next-steps" target="_blank" rel="noreferrer">#</a> Take the next step <a class="header-anchor" href="#take-the-next-step" aria-hidden="true">#</a></h2><p>After reading the overview and completing the <a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/" target="_blank" rel="noreferrer">Getting started</a> tutorial, you should be ready to start writing your own extensions! Dive deeper into the world of custom Chrome with the following resources:</p><ul><li>Learn how to debug Extensions in the <a href="https://developer.chrome.com/docs/extensions/mv3/tut_debugging/" target="_blank" rel="noreferrer">debugging tutorial</a>.</li><li>Chrome Extensions have access to powerful APIs above and beyond what&#39;s available on the open web. The <a href="https://developer.chrome.com/docs/extensions/reference/" target="_blank" rel="noreferrer">chrome APIs documentation</a> will walk through each API.</li><li>The <a href="https://developer.chrome.com/docs/extensions/mv3/devguide/" target="_blank" rel="noreferrer">developer&#39;s guide</a> has dozens of additional links to pieces of documentation relevant to advanced extension creation.</li></ul><p>Updated on Wednesday, November 2, 2022 \u2022 <a href="https://github.com/GoogleChrome/developer.chrome.com/blob/main/site/en/docs/extensions/mv3/architecture-overview/index.md" target="_blank" rel="noreferrer">Improve article</a></p>`,87),a=[s];function i(c,l,h,d,p,m){return r(),t("div",null,a)}const f=e(n,[["render",i]]);export{v as __pageData,f as default};
