import{_ as e,o,c as t,a as n}from"./app.f245760f.js";const u=JSON.parse('{"title":"Development basics","description":"","frontmatter":{},"headers":[{"level":2,"title":"# Overview","slug":"overview","link":"#overview","children":[]},{"level":2,"title":"# Hello Extensions","slug":"hello-extensions","link":"#hello-extensions","children":[]},{"level":2,"title":"# Loading an unpacked extension","slug":"loading-an-unpacked-extension","link":"#loading-an-unpacked-extension","children":[]},{"level":2,"title":"# Pinning the extension","slug":"pinning-the-extension","link":"#pinning-the-extension","children":[]},{"level":2,"title":"# Reloading the extension","slug":"reloading-the-extension","link":"#reloading-the-extension","children":[]},{"level":2,"title":"# Finding console logs and errors","slug":"finding-console-logs-and-errors","link":"#finding-console-logs-and-errors","children":[{"level":3,"title":"# Console logs","slug":"console-logs","link":"#console-logs","children":[]},{"level":3,"title":"# Error logs","slug":"error-logs","link":"#error-logs","children":[]}]},{"level":2,"title":"# Structuring an extension project","slug":"structuring-an-extension-project","link":"#structuring-an-extension-project","children":[]},{"level":2,"title":"# Using Typescript","slug":"using-typescript","link":"#using-typescript","children":[]},{"level":2,"title":"# \u{1F680} Ready to start building?","slug":"\u{1F680}-ready-to-start-building","link":"#\u{1F680}-ready-to-start-building","children":[]}],"relativePath":"chromedocs/Getting Started Guides/Development basics - Chrome Developers.md"}'),r={name:"chromedocs/Getting Started Guides/Development basics - Chrome Developers.md"},s=n(`<ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#overview" target="_blank" rel="noreferrer">Overview</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#build" target="_blank" rel="noreferrer">Hello Extensions</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#load-unpacked" target="_blank" rel="noreferrer">Loading an unpacked extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#pin" target="_blank" rel="noreferrer">Pinning the extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#reload" target="_blank" rel="noreferrer">Reloading the extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#logs" target="_blank" rel="noreferrer">Finding console logs and errors</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#console" target="_blank" rel="noreferrer">Console logs</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#errors" target="_blank" rel="noreferrer">Error logs</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#structure" target="_blank" rel="noreferrer">Structuring an extension project</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#types" target="_blank" rel="noreferrer">Using Typescript</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#building" target="_blank" rel="noreferrer">\u{1F680} Ready to start building?</a></li></ul><h1 id="development-basics" tabindex="-1">Development basics <a class="header-anchor" href="#development-basics" aria-hidden="true">#</a></h1><p>Learn the basics of Chrome extension development.</p><p>Published on Tuesday, October 4, 2022</p><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#overview" target="_blank" rel="noreferrer">Overview</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#build" target="_blank" rel="noreferrer">Hello Extensions</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#load-unpacked" target="_blank" rel="noreferrer">Loading an unpacked extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#pin" target="_blank" rel="noreferrer">Pinning the extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#reload" target="_blank" rel="noreferrer">Reloading the extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#logs" target="_blank" rel="noreferrer">Finding console logs and errors</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#console" target="_blank" rel="noreferrer">Console logs</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#errors" target="_blank" rel="noreferrer">Error logs</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#structure" target="_blank" rel="noreferrer">Structuring an extension project</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#types" target="_blank" rel="noreferrer">Using Typescript</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#building" target="_blank" rel="noreferrer">\u{1F680} Ready to start building?</a></li></ul><h2 id="overview" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#overview" target="_blank" rel="noreferrer">#</a> Overview <a class="header-anchor" href="#overview" aria-hidden="true">#</a></h2><p>This page describes the extension development workflow. We will create a &quot;Hello, Extensions&quot; example, load the extension locally, locate logs, and explore other recommendations.</p><h2 id="hello-extensions" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#build" target="_blank" rel="noreferrer">#</a> Hello Extensions <a class="header-anchor" href="#hello-extensions" aria-hidden="true">#</a></h2><p>This extension will display \u201CHello Extensions\u201D when the user clicks on the extension toolbar icon.</p><p>![Hello extension](./Development basics - Chrome Developers_files/xjKRmWMgwMm7Kdf72bkj.png)</p><p>Hello Extension popup</p><p>Start by creating a new directory to store extension files. If you prefer, you can download the full source code from <a href="https://github.com/GoogleChrome/chrome-extensions-samples/tree/main/tutorials/hello-world" target="_blank" rel="noreferrer">GitHub</a>.</p><p>Next, create a new file in this directory called <code>manifest.json</code> and add the following code:</p><pre><code>{  &quot;manifest_version&quot;: 3,  &quot;name&quot;: &quot;Hello Extensions&quot;,  &quot;description&quot;: &quot;Base Level Extension&quot;,  &quot;version&quot;: &quot;1.0&quot;,  &quot;action&quot;: {    &quot;default_popup&quot;: &quot;hello.html&quot;,    &quot;default_icon&quot;: &quot;hello_extensions.png&quot;  }}
</code></pre><p>This JSON object describes the extension&#39;s capabilities and configuration. For example, the <code>&quot;action&quot;</code> key declares the image Chrome should use as the action&#39;s icon and the HTML page to show in a popup when the action is clicked. <a href="https://storage.googleapis.com/web-dev-uploads/image/WlD8wC6g8khYWPJUsQceQkhXSlv1/gmKIT88Ha1z8VBMJFOOH.png" target="_blank" rel="noreferrer">Download the icon</a> to your directory, and be sure to change its name to match what&#39;s in the <code>&quot;default_icon&quot;</code> key.</p><p>For the popup, create a file named <code>hello.html</code>, and add the following code:</p><pre><code>&lt;html&gt;  &lt;body&gt;    &lt;h1&gt;Hello Extensions&lt;/h1&gt;  &lt;/body&gt;&lt;/html&gt;
</code></pre><p>The extension now displays a popup when the extension action (toolbar icon) is clicked. Let&#39;s test it in Chrome by loading it locally. Ensure all files are saved.</p><h2 id="loading-an-unpacked-extension" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#load-unpacked" target="_blank" rel="noreferrer">#</a> Loading an unpacked extension <a class="header-anchor" href="#loading-an-unpacked-extension" aria-hidden="true">#</a></h2><p>To load an unpacked extension in developer mode:</p><ol><li><p>Go to the Extensions page by entering <code>chrome://extensions</code> in a new tab. (By design <code>chrome://</code> URLs are not linkable.)</p><ul><li>Alternatively, click on the Extensions menu puzzle button and select <strong>Manage Extensions</strong> at the bottom of the menu.</li><li>Or, click the Chrome menu, hover over <strong>More Tools,</strong> then select <strong>Extensions</strong>.</li></ul></li><li><p>Enable Developer Mode by clicking the toggle switch next to <strong>Developer mode</strong>.</p></li><li><p>Click the <strong>Load unpacked</strong> button and select the extension directory.</p><p>![Extensions page](./Development basics - Chrome Developers_files/BzVElZpUtNE4dueVPSp3.png)</p><p>Extensions page (chrome://extensions)</p></li></ol><p>Ta-da! The extension has been successfully installed. Because no extension icons were included in the manifest, a generic icon will be created for the extension.</p><h2 id="pinning-the-extension" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#pin" target="_blank" rel="noreferrer">#</a> Pinning the extension <a class="header-anchor" href="#pinning-the-extension" aria-hidden="true">#</a></h2><p>By default, when you load your extension locally, it will appear in the extensions menu ![Puzzle](./Development basics - Chrome Developers_files/f5i7AgfauCfoQJxnn3kU.png). Pin your extension to the toolbar to quickly access your extension during development.</p><p>![Pinning the extension](./Development basics - Chrome Developers_files/LahIugYHQW3QpHM0z1qZ.png)</p><p>Pinning the extension</p><p>Click on the extension\u2019s action (toolbar icon); you should see a popup.</p><p>![hello world extension](./Development basics - Chrome Developers_files/xjKRmWMgwMm7Kdf72bkj.png)</p><p>Hello World extension</p><h2 id="reloading-the-extension" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#reload" target="_blank" rel="noreferrer">#</a> Reloading the extension <a class="header-anchor" href="#reloading-the-extension" aria-hidden="true">#</a></h2><p>Let\u2019s go back to the code and change the name of the extension to &quot;Hello Extensions of the world!&quot; in the manifest.</p><pre><code>{  &quot;manifest_version&quot;: 3,  &quot;name&quot;: &quot;Hello Extensions of the world!&quot;,  ...}
</code></pre><p>After saving the file, to see this change in the browser you also have to refresh the extension. Go to the Extensions page and click the refresh icon next to the <strong>on/off</strong> toggle:</p><p>![Reload an extension](./Development basics - Chrome Developers_files/4Ph3qL9aUyswxmhauRFB.png)</p><p>\u{1F4A1} <strong>Do I always have to refresh the extension to see my changes?</strong></p><p>Not all components need to be reloaded to see changes made, as shown in the following table:</p><p>Extension component</p><p>Requires extension reload</p><p>The manifest</p><p>Yes</p><p>Service worker</p><p>Yes</p><p>Content Scripts</p><p>Yes (plus the host page)</p><p>The popup</p><p>No</p><p>Options page</p><p>No</p><p>Other extension HTML pages</p><p>No</p><h2 id="finding-console-logs-and-errors" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#logs" target="_blank" rel="noreferrer">#</a> Finding console logs and errors <a class="header-anchor" href="#finding-console-logs-and-errors" aria-hidden="true">#</a></h2><h3 id="console-logs" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#console" target="_blank" rel="noreferrer">#</a> Console logs <a class="header-anchor" href="#console-logs" aria-hidden="true">#</a></h3><p>During development, you can debug your code by accessing the browser console logs. In this case, we will locate the logs for the popup. Start by adding a script tag to <code>hello.html</code>.</p><pre><code>&lt;html&gt;  &lt;body&gt;    &lt;h1&gt;Hello Extensions&lt;/h1&gt;    &lt;script src=&quot;popup.js&quot;&gt;&lt;/script&gt;  &lt;/body&gt;&lt;/html&gt;
</code></pre><p>Create a <code>popup.js</code> file and add the following code:</p><pre><code>console.log(&quot;This is a popup!&quot;)
</code></pre><p>To see this message logged in the Console:</p><ol><li><p>Refresh the extension.</p></li><li><p>Open the popup.</p></li><li><p>Right-click on the popup.</p></li><li><p>Select <strong>Inspect</strong>.</p><p>![Inspecting the popup](./Development basics - Chrome Developers_files/vHGHW1o4J0kZgUkAteRQ.png)</p><p>Inspecting a popup</p></li><li><p>In the <a href="https://developer.chrome.com/docs/devtools/" target="_blank" rel="noreferrer">DevTools</a>, navigate to the <strong>Console</strong> panel.</p></li></ol><p>![DevTools Code Panel](./Development basics - Chrome Developers_files/1ZrcBEYcbMxW1c9UvBy9.png)</p><p>DevTools Console Panel</p><h3 id="error-logs" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#errors" target="_blank" rel="noreferrer">#</a> Error logs <a class="header-anchor" href="#error-logs" aria-hidden="true">#</a></h3><p>Now let&#39;s break the extension. We can do so by removing the closing quote in <code>popup.js</code>:</p><pre><code>console.log(&quot;This is a popup!) // \u274C broken code
</code></pre><p>Go to the Extensions page and open the popup. An <strong>Errors</strong> button will appear.</p><p>![Extensions page with error button](./Development basics - Chrome Developers_files/a9lAHCJZZrebOSKrkPRD.png)</p><p>Click the <strong>Errors</strong> button to learn more about the error:</p><p>![Extension error details](./Development basics - Chrome Developers_files/GrorLyaC6nRAyqc1qULC.png)</p><p>To learn more about debugging the service worker, options page, and content scripts, see <a href="https://developer.chrome.com/docs/extensions/mv3/tut_debugging/" target="_blank" rel="noreferrer">Debugging extensions</a>.</p><h2 id="structuring-an-extension-project" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#structure" target="_blank" rel="noreferrer">#</a> Structuring an extension project <a class="header-anchor" href="#structuring-an-extension-project" aria-hidden="true">#</a></h2><p>There are many ways to structure an extension project; however, you must place the manifest.json file in the extension&#39;s <strong>root directory</strong>. The following is a structure example:</p><p>![The contents of an extension folder: manifest.json, background.js, scripts folder, popup folder, and images folder.](./Development basics - Chrome Developers_files/hjccQNanPjTDpIajkhPU.png)</p><h2 id="using-typescript" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#types" target="_blank" rel="noreferrer">#</a> Using Typescript <a class="header-anchor" href="#using-typescript" aria-hidden="true">#</a></h2><p>If you are developing using a <a href="https://developer.mozilla.org/docs/Glossary/IDE" target="_blank" rel="noreferrer">code editor</a> such as VSCode or Atom, you can use the npm package <a href="https://www.npmjs.com/package/chrome-types" target="_blank" rel="noreferrer">chrome-types</a> to take advantage of auto-completion for the <a href="https://developer.chrome.com/docs/extensions/reference/" target="_blank" rel="noreferrer">Chrome API</a>. This npm package is updated automatically when the Chromium source code changes.</p><p>Gotchas</p><p>Upgrade this npm package frequently to work with the latest Chromium version.</p><h2 id="\u{1F680}-ready-to-start-building" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/#building" target="_blank" rel="noreferrer">#</a> \u{1F680} Ready to start building? <a class="header-anchor" href="#\u{1F680}-ready-to-start-building" aria-hidden="true">#</a></h2><p>Choose any of the following tutorials to begin your extension learning journey.</p><p>Extension</p><p>What you will learn</p><p><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/" target="_blank" rel="noreferrer">Reading time</a></p><p>To insert an element on every page automatically.</p><p><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-focus-mode/" target="_blank" rel="noreferrer">Focus Mode</a></p><p>To run code on the current page after clicking on the extension action.</p><p><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-tabs-manager/" target="_blank" rel="noreferrer">Tabs Manager</a></p><p>To create a popup that manages browser tabs.</p><p>As a bonus, these tutorials were designed to improve your experience when reading the Chrome extension and Chrome Web store documentation:</p><ul><li>Reading time adds the expected reading time to each documentation articles.</li><li>Focus mode changes the style of the page to help you concentrate on the documentation content.</li><li>Tabs manager allows you to organize your extension documentation tabs.</li></ul><p>Updated on Tuesday, October 4, 2022 \u2022 <a href="https://github.com/GoogleChrome/developer.chrome.com/blob/main/site/en/docs/extensions/mv3/getstarted/development-basics/index.md" target="_blank" rel="noreferrer">Improve article</a></p>`,88),a=[s];function l(i,p,c,d,h,g){return o(),t("div",null,a)}const v=e(r,[["render",l]]);export{u as __pageData,v as default};
