import{_ as e,o as t,c as r,a as o}from"./app.04ed3e28.js";const u=JSON.parse('{"title":"Extensions 101","description":"","frontmatter":{},"headers":[{"level":2,"title":"# Overview","slug":"overview","link":"#overview","children":[]},{"level":2,"title":"# Web technologies","slug":"web-technologies","link":"#web-technologies","children":[]},{"level":2,"title":"# Chrome extension APIs","slug":"chrome-extension-apis","link":"#chrome-extension-apis","children":[]},{"level":2,"title":"# Extension files","slug":"extension-files","link":"#extension-files","children":[]},{"level":2,"title":"# Developing your extension","slug":"developing-your-extension","link":"#developing-your-extension","children":[]},{"level":2,"title":"# Designing your extension features","slug":"designing-your-extension-features","link":"#designing-your-extension-features","children":[]},{"level":2,"title":"# Distributing your extension","slug":"distributing-your-extension","link":"#distributing-your-extension","children":[]},{"level":2,"title":"# \u{1F680} Ready to start building?","slug":"\u{1F680}-ready-to-start-building","link":"#\u{1F680}-ready-to-start-building","children":[]}],"relativePath":"chromedocs/Getting Started Guides/Extensions 101 - Chrome Developers.md","lastUpdated":1679926139000}'),n={name:"chromedocs/Getting Started Guides/Extensions 101 - Chrome Developers.md"},s=o('<ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#overview" target="_blank" rel="noreferrer">Overview</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#web-tech" target="_blank" rel="noreferrer">Web technologies</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#chrome-apis" target="_blank" rel="noreferrer">Chrome extension APIs</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#extension-files" target="_blank" rel="noreferrer">Extension files</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#development" target="_blank" rel="noreferrer">Developing your extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#quality" target="_blank" rel="noreferrer">Designing your extension features</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#distribution" target="_blank" rel="noreferrer">Distributing your extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#building" target="_blank" rel="noreferrer">\u{1F680} Ready to start building?</a></li></ul><h1 id="extensions-101" tabindex="-1">Extensions 101 <a class="header-anchor" href="#extensions-101" aria-hidden="true">#</a></h1><p>Explore basic concepts of Chrome extension development.</p><p>Published on Tuesday, October 4, 2022</p><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#overview" target="_blank" rel="noreferrer">Overview</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#web-tech" target="_blank" rel="noreferrer">Web technologies</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#chrome-apis" target="_blank" rel="noreferrer">Chrome extension APIs</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#extension-files" target="_blank" rel="noreferrer">Extension files</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#development" target="_blank" rel="noreferrer">Developing your extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#quality" target="_blank" rel="noreferrer">Designing your extension features</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#distribution" target="_blank" rel="noreferrer">Distributing your extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#building" target="_blank" rel="noreferrer">\u{1F680} Ready to start building?</a></li></ul><h2 id="overview" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#overview" target="_blank" rel="noreferrer">#</a> Overview <a class="header-anchor" href="#overview" aria-hidden="true">#</a></h2><p>Chrome extensions enhance the browsing experience by adding features and functionality to the Chrome browser. This page introduces Chrome extension development and provides links to step-by-step <a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#building" target="_blank" rel="noreferrer">beginner tutorials</a>.</p><h2 id="web-technologies" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#web-tech" target="_blank" rel="noreferrer">#</a> Web technologies <a class="header-anchor" href="#web-technologies" aria-hidden="true">#</a></h2><p>Extensions are written with the same web technologies used to create web applications:</p><ul><li><a href="https://web.dev/learn/html/" target="_blank" rel="noreferrer">HTML</a> is used as a content markup language.</li><li><a href="https://web.dev/learn/css/" target="_blank" rel="noreferrer">CSS</a> is used for styling.</li><li><a href="https://developer.mozilla.org/docs/Learn/JavaScript" target="_blank" rel="noreferrer">JavaScript</a> is used for scripting and logic.</li></ul><p>Before moving forward, we recommend that you become familiar with these technologies.</p><h2 id="chrome-extension-apis" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#chrome-apis" target="_blank" rel="noreferrer">#</a> Chrome extension APIs <a class="header-anchor" href="#chrome-extension-apis" aria-hidden="true">#</a></h2><p>Extensions can use all the <a href="https://developer.mozilla.org/docs/Web/API" target="_blank" rel="noreferrer">JavaScript APIs</a> that the browser provides. What makes extensions more powerful than a web app is their access to <a href="https://developer.chrome.com/docs/extensions/reference/" target="_blank" rel="noreferrer">Chrome APIs</a>. The following are just a few examples of what extensions can do:</p><ul><li>Change the functionality or behavior of a website.</li><li>Allow users to collect and organize information across websites.</li><li>Add features to Chrome DevTools.</li></ul><p>See <a href="https://developer.chrome.com/docs/extensions/mv3/devguide/" target="_blank" rel="noreferrer">Extension development overview</a> for a complete list of API capabilities.</p><h2 id="extension-files" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#extension-files" target="_blank" rel="noreferrer">#</a> Extension files <a class="header-anchor" href="#extension-files" aria-hidden="true">#</a></h2><p>Extensions contain different files, depending on the functionality provided. The following are some of the most frequently used files:</p><p>The manifest</p><p>The extension&#39;s <a href="https://developer.chrome.com/docs/extensions/mv3/manifest/" target="_blank" rel="noreferrer">manifest</a> is the only required file that <strong>must</strong> have a specific file name: <code>manifest.json</code> . It also has to be located in the extension&#39;s root directory. The manifest records important metadata, defines resources, declares permissions, and identifies which files to run in the background and on the page.</p><p>The service worker</p><p>The extension <a href="https://developer.chrome.com/docs/extensions/mv3/service_workers/" target="_blank" rel="noreferrer">service worker</a> handles and listens for browser events. There are many types of events, such as navigating to a new page, removing a bookmark, or closing a tab. It can use all the <a href="https://developer.chrome.com/docs/extensions/reference/" target="_blank" rel="noreferrer">Chrome APIs</a>, but it cannot interact directly with the content of web pages; that\u2019s the job of content scripts.</p><p>Content scripts</p><p><a href="https://developer.chrome.com/docs/extensions/mv3/content_scripts/" target="_blank" rel="noreferrer">Content scripts</a> execute Javascript in the context of a web page. They can also read and modify the <a href="https://developer.mozilla.org/docs/Web/API/Document_Object_Model" target="_blank" rel="noreferrer">DOM</a> of the pages they&#39;re injected into. Content Scripts can only use a subset of the <a href="https://developer.chrome.com/docs/extensions/reference/" target="_blank" rel="noreferrer">Chrome APIs</a> but can indirectly access the rest by exchanging messages with the extension service worker.</p><p>The popup and other pages</p><p>An extension can include various HTML files, such as a <a href="https://developer.chrome.com/docs/extensions/mv3/user_interface/#popup" target="_blank" rel="noreferrer">popup</a>, an <a href="https://developer.chrome.com/docs/extensions/mv3/options/" target="_blank" rel="noreferrer">options page</a>, and <a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/#html-files" target="_blank" rel="noreferrer">other HTML pages</a>. All these pages have access to <a href="https://developer.chrome.com/docs/extensions/reference/" target="_blank" rel="noreferrer">Chrome APIs</a>.</p><p>Visit <a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/" target="_blank" rel="noreferrer">Extensions Architecture</a> and <a href="https://developer.chrome.com/docs/extensions/mv3/user_interface/" target="_blank" rel="noreferrer">Designing the user interface</a> to dive deeper.</p><p>\u{1F4A1} <strong>Do all extensions have a popup?</strong></p><p>Many extensions use a popup to customize the user experience, however this is <em>not</em> required. For example, the <a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/" target="_blank" rel="noreferrer">reading time</a> and <a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-focus-mode/" target="_blank" rel="noreferrer">focus mode</a> extension tutorials do not include a popup.</p><h2 id="developing-your-extension" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#development" target="_blank" rel="noreferrer">#</a> Developing your extension <a class="header-anchor" href="#developing-your-extension" aria-hidden="true">#</a></h2><p>Even though web applications and extensions share many of the same technologies, the extension development experience is different. Check out <a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/" target="_blank" rel="noreferrer">Development Basics</a> to create a &quot;Hello, Extensions&quot; example and familiarize yourself with the extension development workflow.</p><h2 id="designing-your-extension-features" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#quality" target="_blank" rel="noreferrer">#</a> Designing your extension features <a class="header-anchor" href="#designing-your-extension-features" aria-hidden="true">#</a></h2><p>When you start designing your extension and choosing which features to support, make sure it fulfills a <a href="https://developer.chrome.com/docs/extensions/mv3/single_purpose/" target="_blank" rel="noreferrer">single purpose</a> that is narrowly defined and easy to understand. This will allow your extension to be distributed through the Chrome Web Store.</p><p><strong>\u{1F4A1} What exactly does &quot;single purpose&quot; mean?</strong></p><p>&quot;Single purpose&quot; can refer to one of two aspects of an extension:</p><ol><li><p>An extension can have a single purpose limited to a narrow <em>focus area</em> or <em>subject matter</em>. For example, &quot;news headlines&quot;, &quot;weather&quot;, &quot;comparison shopping&quot;.</p></li><li><p>Or, an extension can have a single purpose limited to a narrow <em>browser function</em>. For example, &quot;new tab page&quot;, &quot;tab management&quot;, or &quot;search provider&quot;.</p></li></ol><p>Regardless of the extension&#39;s purpose, the experience provided by the extension must respect the user&#39;s other settings and preferences.</p><p>See <a href="https://developer.chrome.com/docs/extensions/mv3/single_purpose/" target="_blank" rel="noreferrer">Extension quality guidelines</a> for additional details.</p><h2 id="distributing-your-extension" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#distribution" target="_blank" rel="noreferrer">#</a> Distributing your extension <a class="header-anchor" href="#distributing-your-extension" aria-hidden="true">#</a></h2><p>You can set up a developer account with the <a href="https://chrome.google.com/webstore/" target="_blank" rel="noreferrer">Chrome Web Store</a> to host and distribute your extension. Bare in mind that extensions must adhere to the <a href="https://developer.chrome.com/docs/webstore/program-policies/" target="_blank" rel="noreferrer">developer program policies</a>.</p><p>See <a href="https://developer.chrome.com/docs/webstore/publish/" target="_blank" rel="noreferrer">Publish in the Chrome Web Store</a> to learn how to distribute your extension.</p><h2 id="\u{1F680}-ready-to-start-building" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/extensions-101/#building" target="_blank" rel="noreferrer">#</a> \u{1F680} Ready to start building? <a class="header-anchor" href="#\u{1F680}-ready-to-start-building" aria-hidden="true">#</a></h2><p>Choose any of the following tutorials to begin your extension learning journey.</p><p>Extension</p><p>What you will learn</p><p><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/" target="_blank" rel="noreferrer">Reading time</a></p><p>To insert an element on every page automatically.</p><p><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-focus-mode/" target="_blank" rel="noreferrer">Focus Mode</a></p><p>To run code on the current page after clicking on the extension action.</p><p><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-tabs-manager/" target="_blank" rel="noreferrer">Tabs Manager</a></p><p>To create a popup that manages browser tabs.</p><p>As a bonus, these tutorials were designed to improve your experience when reading the Chrome extension and Chrome Web store documentation:</p><ul><li>Reading time adds the expected reading time to each documentation articles.</li><li>Focus mode changes the style of the page to help you concentrate on the documentation content.</li><li>Tabs manager allows you to organize your extension documentation tabs.</li></ul><p>Updated on Tuesday, October 4, 2022 \u2022 <a href="https://github.com/GoogleChrome/developer.chrome.com/blob/main/site/en/docs/extensions/mv3/getstarted/extensions-101/index.md" target="_blank" rel="noreferrer">Improve article</a></p>',53),a=[s];function i(l,h,d,c,p,m){return t(),r("div",null,a)}const f=e(n,[["render",i]]);export{u as __pageData,f as default};
