import{_ as e,o as t,c as r,a as o}from"./app.8d96fb22.js";const g=JSON.parse('{"title":"Reading time","description":"","frontmatter":{},"headers":[{"level":2,"title":"# Overview","slug":"overview","link":"#overview","children":[]},{"level":2,"title":"# Before you start","slug":"before-you-start","link":"#before-you-start","children":[]},{"level":2,"title":"# Build the extension","slug":"build-the-extension","link":"#build-the-extension","children":[{"level":3,"title":"# Step 1: Add information about the extension","slug":"step-1-add-information-about-the-extension","link":"#step-1-add-information-about-the-extension","children":[]},{"level":3,"title":"# Step 2: Provide the icons","slug":"step-2-provide-the-icons","link":"#step-2-provide-the-icons","children":[]},{"level":3,"title":"# Step 3: Declare the content script","slug":"step-3-declare-the-content-script","link":"#step-3-declare-the-content-script","children":[]},{"level":3,"title":"# Step 4: Calculate and insert the reading time","slug":"step-4-calculate-and-insert-the-reading-time","link":"#step-4-calculate-and-insert-the-reading-time","children":[]}]},{"level":2,"title":"# Test that it works","slug":"test-that-it-works","link":"#test-that-it-works","children":[{"level":3,"title":"# Load your extension locally","slug":"load-your-extension-locally","link":"#load-your-extension-locally","children":[]},{"level":3,"title":"# Open an extension or Chrome Web Store documentation","slug":"open-an-extension-or-chrome-web-store-documentation","link":"#open-an-extension-or-chrome-web-store-documentation","children":[]}]},{"level":2,"title":"# \u{1F3AF} Potential enhancements","slug":"\u{1F3AF}-potential-enhancements","link":"#\u{1F3AF}-potential-enhancements","children":[]},{"level":2,"title":"# Keep building!","slug":"keep-building","link":"#keep-building","children":[]},{"level":2,"title":"# Continue exploring","slug":"continue-exploring","link":"#continue-exploring","children":[]}],"relativePath":"chromedocs/Getting Started Guides/Reading time - Chrome Developers.md","lastUpdated":1679926139000}'),n={name:"chromedocs/Getting Started Guides/Reading time - Chrome Developers.md"},a=o('<ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#overview" target="_blank" rel="noreferrer">Overview</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#prereq" target="_blank" rel="noreferrer">Before you start</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#build" target="_blank" rel="noreferrer">Build the extension</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#step-1" target="_blank" rel="noreferrer">Step 1: Add information about the extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#step-2" target="_blank" rel="noreferrer">Step 2: Provide the icons</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#step-3" target="_blank" rel="noreferrer">Step 3: Declare the content script</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#step-4" target="_blank" rel="noreferrer">Step 4: Calculate and insert the reading time</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#try-out" target="_blank" rel="noreferrer">Test that it works</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#locally" target="_blank" rel="noreferrer">Load your extension locally</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#open-sites" target="_blank" rel="noreferrer">Open an extension or Chrome Web Store documentation</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#challenge" target="_blank" rel="noreferrer">\u{1F3AF} Potential enhancements</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#continue" target="_blank" rel="noreferrer">Keep building!</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#continue-exploring" target="_blank" rel="noreferrer">Continue exploring</a></li></ul><h1 id="reading-time" tabindex="-1">Reading time <a class="header-anchor" href="#reading-time" aria-hidden="true">#</a></h1><p>Create your first extension that inserts a new element on the page.</p><p>Published on Tuesday, October 4, 2022</p><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#overview" target="_blank" rel="noreferrer">Overview</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#prereq" target="_blank" rel="noreferrer">Before you start</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#build" target="_blank" rel="noreferrer">Build the extension</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#step-1" target="_blank" rel="noreferrer">Step 1: Add information about the extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#step-2" target="_blank" rel="noreferrer">Step 2: Provide the icons</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#step-3" target="_blank" rel="noreferrer">Step 3: Declare the content script</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#step-4" target="_blank" rel="noreferrer">Step 4: Calculate and insert the reading time</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#try-out" target="_blank" rel="noreferrer">Test that it works</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#locally" target="_blank" rel="noreferrer">Load your extension locally</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#open-sites" target="_blank" rel="noreferrer">Open an extension or Chrome Web Store documentation</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#challenge" target="_blank" rel="noreferrer">\u{1F3AF} Potential enhancements</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#continue" target="_blank" rel="noreferrer">Keep building!</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#continue-exploring" target="_blank" rel="noreferrer">Continue exploring</a></li></ul><h2 id="overview" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#overview" target="_blank" rel="noreferrer">#</a> Overview <a class="header-anchor" href="#overview" aria-hidden="true">#</a></h2><p>This tutorial builds an extension that adds the expected reading time to any Chrome extension and Chrome Web Store documentation page.</p><p>![Reading time extension in the extension&#39;s Welcome page](./Reading time - Chrome Developers_files/VczSGe8eh0Xv7nTXxhxg.png)</p><p>Reading time extension on the extension&#39;s Welcome page.</p><p>In this guide, we\u2019re going to explain the following concepts:</p><ul><li>The extension manifest.</li><li>What icon sizes an extension uses.</li><li>How to inject code into pages using <a href="https://developer.chrome.com/docs/extensions/mv3/content_scripts/" target="_blank" rel="noreferrer">content scripts</a>.</li><li>How to use match patterns.</li><li>Extension permissions.</li></ul><h2 id="before-you-start" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#prereq" target="_blank" rel="noreferrer">#</a> Before you start <a class="header-anchor" href="#before-you-start" aria-hidden="true">#</a></h2><p>This guide assumes that you have basic web development experience. We recommend checking out <a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics/" target="_blank" rel="noreferrer">Development Basics</a> for an introduction to the extension development workflow.</p><h2 id="build-the-extension" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#build" target="_blank" rel="noreferrer">#</a> Build the extension <a class="header-anchor" href="#build-the-extension" aria-hidden="true">#</a></h2><p>To start, create a new directory called <code>reading-time</code> to hold the extension&#39;s files. If you prefer, you can download the complete source code from <a href="https://github.com/GoogleChrome/chrome-extensions-samples/tree/main/tutorials/reading-time" target="_blank" rel="noreferrer">GitHub</a>.</p><h3 id="step-1-add-information-about-the-extension" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#step-1" target="_blank" rel="noreferrer">#</a> Step 1: Add information about the extension <a class="header-anchor" href="#step-1-add-information-about-the-extension" aria-hidden="true">#</a></h3><p>The manifest JSON file is the only required file. It holds important information about the extension. Create a <code>manifest.json</code> file in the <em>root</em> of the project and add the following code:</p><pre><code>{  &quot;manifest_version&quot;: 3,  &quot;name&quot;: &quot;Reading time&quot;,  &quot;version&quot;: &quot;1.0&quot;,  &quot;description&quot;: &quot;Add the reading time to Chrome Extension documentation articles&quot;,}\n</code></pre><p>These keys contain basic metadata for the extension. They control how the extension appears on the Extensions page and, when published, on the Chrome Web Store. To dive deeper, check out the <a href="https://developer.chrome.com/docs/extensions/mv3/manifest/name/" target="_blank" rel="noreferrer"><code>&quot;name&quot;</code></a>, <a href="https://developer.chrome.com/docs/extensions/mv3/manifest/version/" target="_blank" rel="noreferrer"><code>&quot;version&quot;</code></a> and <a href="https://developer.chrome.com/docs/extensions/mv3/manifest/description/" target="_blank" rel="noreferrer"><code>&quot;description&quot;</code></a> keys on the <a href="https://developer.chrome.com/docs/extensions/mv3/manifest/" target="_blank" rel="noreferrer">Manifest</a> overview page.</p><p>\u{1F4A1} <strong>Other facts about the extension manifest</strong></p><ul><li>It must be located at the <strong>root</strong> of the project.</li><li>The only required keys are the <code>&quot;manifest_version&quot;</code>, <code>&quot;name&quot;</code>, and <code>&quot;version&quot;</code>.</li><li>It supports comments (<code>//</code>) during development, but these must be removed before uploading your code to the Chrome Web Store.</li></ul><h3 id="step-2-provide-the-icons" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#step-2" target="_blank" rel="noreferrer">#</a> Step 2: Provide the icons <a class="header-anchor" href="#step-2-provide-the-icons" aria-hidden="true">#</a></h3><p>So, why do we need icons? Although <a href="https://developer.chrome.com/docs/extensions/mv3/manifest/icons/" target="_blank" rel="noreferrer">icons</a> are optional during development, they are required if you plan to distribute your extension on the Chrome Web Store. They also appear in other places like the Extensions page.</p><p>Create an <code>images</code> folder and place the icons inside. You can download the icons on <a href="https://github.com/GoogleChrome/chrome-extensions-samples/tree/main/tutorials/reading-time/images" target="_blank" rel="noreferrer">GitHub</a>. Next, add the highlighted code to your manifest to declare icons:</p><pre><code>{  ...  &quot;icons&quot;: {    &quot;16&quot;: &quot;images/icon-16.png&quot;,    &quot;32&quot;: &quot;images/icon-32.png&quot;,    &quot;48&quot;: &quot;images/icon-48.png&quot;,    &quot;128&quot;: &quot;images/icon-128.png&quot;  }  ...}\n</code></pre><p>We recommend using PNG files, but other file formats are allowed, except for SVG files.</p><p>\u{1F4A1} <strong>Where are these differently-sized icons displayed?</strong></p><p>Icon size</p><p>Icon use</p><p>16x16</p><p>Favicon on the extension&#39;s pages and context menu icon.</p><p>32x32</p><p>Windows computers often require this size.</p><p>48x48</p><p>Displays on the Extensions page.</p><p>128x128</p><p>Displays on installation and in the Chrome Web Store.</p><h3 id="step-3-declare-the-content-script" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#step-3" target="_blank" rel="noreferrer">#</a> Step 3: Declare the content script <a class="header-anchor" href="#step-3-declare-the-content-script" aria-hidden="true">#</a></h3><p>Extensions can run scripts that read and modify the content of a page. These are called <em>content scripts</em>. They live in an <a href="https://developer.chrome.com/docs/extensions/mv3/content_scripts/#isolated_world" target="_blank" rel="noreferrer">isolated world</a>, meaning they can make changes to their JavaScript environment without conflicting with their host page or other extension&#39;s content scripts.</p><p>Add the following code to the <code>manifest.json</code> to register a content script called <code>content.js</code>.</p><pre><code>{  ...  &quot;content_scripts&quot;: [    {      &quot;js&quot;: [&quot;scripts/content.js&quot;],      &quot;matches&quot;: [        &quot;https://developer.chrome.com/docs/extensions/*&quot;,        &quot;https://developer.chrome.com/docs/webstore/*&quot;      ]    }  ]}\n</code></pre><p>The <code>&quot;matches&quot;</code> field can have one or more <a href="https://developer.chrome.com/docs/extensions/mv3/match_patterns/" target="_blank" rel="noreferrer">match patterns</a>. These allow the browser to identify which sites to inject the content scripts into. Match patterns consist of three parts <code>&lt;scheme&gt;://&lt;host&gt;&lt;path&gt;</code>. They can contain &#39;<code>*</code>&#39; characters.</p><p>\u{1F4A1} <strong>Does this extension display a permission warning?</strong></p><p>When a user installs an extension, the browser informs them what the extension can do. Content scripts request permission to run on sites that meet the match pattern criteria.</p><p>In this example, the user would see the following permission warning:</p><p>![Permission warning the user will see when installing the Reading time extension](./Reading time - Chrome Developers_files/rKDdOyri9x8VkhTEXbO6.png)</p><p>Reading time permission warning.</p><p>To dive deeper on extension permissions, see <a href="https://developer.chrome.com/docs/extensions/mv3/permission_warnings/" target="_blank" rel="noreferrer">Declaring permissions and warn users</a>.</p><h3 id="step-4-calculate-and-insert-the-reading-time" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#step-4" target="_blank" rel="noreferrer">#</a> Step 4: Calculate and insert the reading time <a class="header-anchor" href="#step-4-calculate-and-insert-the-reading-time" aria-hidden="true">#</a></h3><p>Content scripts can use the standard <a href="https://developer.mozilla.org/docs/Web/API/Document_Object_Model" target="_blank" rel="noreferrer">Document Object Model</a> (DOM) to read and change the content of a page. The extension will first check if the page contains the <code>&lt;article&gt;</code> element. Then, it will count all the words within this element and create a paragraph that displays the total reading time.</p><p>Create a file called <code>content.js</code> inside a folder called <code>scripts</code> and add the following code:</p><pre><code>const article = document.querySelector(&quot;article&quot;);// `document.querySelector` may return null if the selector doesn&#39;t match anything.if (article) {  const text = article.textContent;  const wordMatchRegExp = /[^\\s]+/g; // Regular expression  const words = text.matchAll(wordMatchRegExp);  // matchAll returns an iterator, convert to array to get word count  const wordCount = [...words].length;  const readingTime = Math.round(wordCount / 200);  const badge = document.createElement(&quot;p&quot;);  // Use the same styling as the publish information in an article&#39;s header  badge.classList.add(&quot;color-secondary-text&quot;, &quot;type--caption&quot;);  badge.textContent = `\u23F1\uFE0F ${readingTime} min read`;  // Support for API reference docs  const heading = article.querySelector(&quot;h1&quot;);  // Support for article docs with date  const date = article.querySelector(&quot;time&quot;)?.parentNode;  (date ?? heading).insertAdjacentElement(&quot;afterend&quot;, badge);}\n</code></pre><p>\u{1F4A1} <strong>Interesting JavaScript used in this code</strong></p><ul><li><a href="https://developer.mozilla.org/docs/Web/JavaScript/Guide/Regular_Expressions#writing_a_regular_expression_pattern" target="_blank" rel="noreferrer">Regular expressions</a> used to count only the words inside the <code>&lt;article&gt;</code> element.</li><li><a href="https://developer.mozilla.org/docs/Web/API/Element/insertAdjacentElement" target="_blank" rel="noreferrer">InsertAdjacentElement()</a> used to insert the reading time node after the element.</li><li>The <a href="https://developer.mozilla.org/docs/Web/API/Element/classList" target="_blank" rel="noreferrer">Classlist</a> property used to add CSS class names to the element class attribute.</li><li><a href="https://developer.mozilla.org/docs/Web/JavaScript/Reference/Operators/Optional_chaining" target="_blank" rel="noreferrer">Optional chaining</a> used to access an object property that may be undefined or null.</li><li><a href="https://developer.mozilla.org/docs/Web/JavaScript/Reference/Operators/Nullish_coalescing_operator" target="_blank" rel="noreferrer">Nullish coalescing</a> returns the <code>&lt;heading&gt;</code> if the <code>&lt;date&gt;</code> is null or undefined.</li></ul><h2 id="test-that-it-works" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#try-out" target="_blank" rel="noreferrer">#</a> Test that it works <a class="header-anchor" href="#test-that-it-works" aria-hidden="true">#</a></h2><p>Verify that the file structure of your project looks like the following:</p><p>![The contents of the reading time folder: manifest.json, content.js in scripts folder, and images folder.](./Reading time - Chrome Developers_files/clhDe13hHGwiNyuRczzk.png)</p><h3 id="load-your-extension-locally" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#locally" target="_blank" rel="noreferrer">#</a> Load your extension locally <a class="header-anchor" href="#load-your-extension-locally" aria-hidden="true">#</a></h3><p>To load an unpacked extension in developer mode, follow the steps in <a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/development-basics#load-unpacked" target="_blank" rel="noreferrer">Development Basics</a>.</p><h3 id="open-an-extension-or-chrome-web-store-documentation" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#open-sites" target="_blank" rel="noreferrer">#</a> Open an extension or Chrome Web Store documentation <a class="header-anchor" href="#open-an-extension-or-chrome-web-store-documentation" aria-hidden="true">#</a></h3><p>Here are a few pages you can open to see how long each article will take to read.</p><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/" target="_blank" rel="noreferrer">Welcome to the Chrome Extension documentation</a></li><li><a href="https://developer.chrome.com/docs/webstore/publish/" target="_blank" rel="noreferrer">Publish in the Chrome Web Store</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/content_scripts/" target="_blank" rel="noreferrer">Understanding Content Scripts</a></li></ul><p>It should look like this:</p><p>![Reading time running on the Welcome page](./Reading time - Chrome Developers_files/VczSGe8eh0Xv7nTXxhxg.png)</p><p>Extension Welcome page with the Reading time extension</p><h2 id="\u{1F3AF}-potential-enhancements" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#challenge" target="_blank" rel="noreferrer">#</a> \u{1F3AF} Potential enhancements <a class="header-anchor" href="#\u{1F3AF}-potential-enhancements" aria-hidden="true">#</a></h2><p>Based on what you\u2019ve learned today, try to implement any of the following:</p><ul><li>Add another <strong>match pattern</strong> in the manifest.json to support other <a href="https://developer.chrome.com/docs/" target="_blank" rel="noreferrer">chrome developer</a> pages, like for example, the <a href="https://developer.chrome.com/docs/devtools/" target="_blank" rel="noreferrer">Chrome DevTools</a> or <a href="https://developer.chrome.com/docs/workbox/" target="_blank" rel="noreferrer">Workbox</a>.</li><li>Add a new content script that calculates the reading time to any of your favorite blogs or documentation sites.</li></ul><p>\u{1F4A1} <strong>HINT</strong>: You can use the DevTools to <a href="https://developer.chrome.com/docs/devtools/dom/" target="_blank" rel="noreferrer">inspect DOM elements</a>.</p><h2 id="keep-building" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#continue" target="_blank" rel="noreferrer">#</a> Keep building! <a class="header-anchor" href="#keep-building" aria-hidden="true">#</a></h2><p>Congratulations on finishing this tutorial \u{1F389}. Continue building your skills by completing other tutorials on this series:</p><p>Extension</p><p>What you will learn</p><p><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-focus-mode/" target="_blank" rel="noreferrer">Focus Mode</a></p><p>To run code on the current page after clicking on the extension action.</p><p><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-tabs-manager/" target="_blank" rel="noreferrer">Tabs Manager</a></p><p>To create a popup that manages browser tabs.</p><h2 id="continue-exploring" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/getstarted/tut-reading-time/#continue-exploring" target="_blank" rel="noreferrer">#</a> Continue exploring <a class="header-anchor" href="#continue-exploring" aria-hidden="true">#</a></h2><p>We hope you enjoyed building this Chrome extension and are excited to continue your Chrome development learning journey. We recommend the following learning path:</p><ul><li>The <a href="https://developer.chrome.com/docs/extensions/mv3/architecture-overview/" target="_blank" rel="noreferrer">Chrome Extension Architecture</a> backs up a bit and fills in a lot of detail about the Extensions architecture in general.</li><li>The <a href="https://developer.chrome.com/docs/extensions/mv3/devguide/" target="_blank" rel="noreferrer">developer&#39;s guide</a> has dozens of additional links to pieces of documentation relevant to advanced extension creation.</li><li>Extensions have access to powerful APIs beyond what&#39;s available on the open web. The <a href="https://developer.chrome.com/docs/extensions/reference/" target="_blank" rel="noreferrer">Chrome APIs documentation</a> walks through each API.</li></ul><p>Updated on Tuesday, October 4, 2022 \u2022 <a href="https://github.com/GoogleChrome/developer.chrome.com/blob/main/site/en/docs/extensions/mv3/getstarted/tut-reading-time/index.md" target="_blank" rel="noreferrer">Improve article</a></p>',81),i=[a];function s(l,d,c,h,p,m){return t(),r("div",null,i)}const f=e(n,[["render",s]]);export{g as __pageData,f as default};
