import{_ as e,o,c as t,a as n}from"./app.8d96fb22.js";const g=JSON.parse(`{"title":"Extending DevTools","description":"","frontmatter":{},"headers":[{"level":2,"title":"# Overview","slug":"overview","link":"#overview","children":[]},{"level":2,"title":"# The DevTools page","slug":"the-devtools-page","link":"#the-devtools-page","children":[]},{"level":2,"title":"# Creating a DevTools extension","slug":"creating-a-devtools-extension","link":"#creating-a-devtools-extension","children":[]},{"level":2,"title":"# DevTools UI elements: panels and sidebar panes","slug":"devtools-ui-elements-panels-and-sidebar-panes","link":"#devtools-ui-elements-panels-and-sidebar-panes","children":[]},{"level":2,"title":"# Communicating between extension components","slug":"communicating-between-extension-components","link":"#communicating-between-extension-components","children":[{"level":3,"title":"# Injecting a content script","slug":"injecting-a-content-script","link":"#injecting-a-content-script","children":[]},{"level":3,"title":"# Evaluating JavaScript in the inspected window","slug":"evaluating-javascript-in-the-inspected-window","link":"#evaluating-javascript-in-the-inspected-window","children":[]},{"level":3,"title":"# Passing the selected element to a content script","slug":"passing-the-selected-element-to-a-content-script","link":"#passing-the-selected-element-to-a-content-script","children":[]},{"level":3,"title":"# Getting a reference panel's window","slug":"getting-a-reference-panel-s-window","link":"#getting-a-reference-panel-s-window","children":[]},{"level":3,"title":"# Messaging from content scripts to the DevTools page","slug":"messaging-from-content-scripts-to-the-devtools-page","link":"#messaging-from-content-scripts-to-the-devtools-page","children":[]},{"level":3,"title":"# Messaging from injected scripts to the DevTools page","slug":"messaging-from-injected-scripts-to-the-devtools-page","link":"#messaging-from-injected-scripts-to-the-devtools-page","children":[]},{"level":3,"title":"# Detecting when DevTools opens and closes","slug":"detecting-when-devtools-opens-and-closes","link":"#detecting-when-devtools-opens-and-closes","children":[]}]},{"level":2,"title":"# DevTools extension examples","slug":"devtools-extension-examples","link":"#devtools-extension-examples","children":[]},{"level":2,"title":"# More information","slug":"more-information","link":"#more-information","children":[]},{"level":2,"title":"# Examples","slug":"examples","link":"#examples","children":[]}],"relativePath":"chromedocs/In depth_ more topics/Extending DevTools - Chrome Developers.md","lastUpdated":1679926139000}`),s={name:"chromedocs/In depth_ more topics/Extending DevTools - Chrome Developers.md"},r=n(`<ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#overview" target="_blank" rel="noreferrer">Overview</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#devtools-page" target="_blank" rel="noreferrer">The DevTools page</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#creating" target="_blank" rel="noreferrer">Creating a DevTools extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#devtools-ui" target="_blank" rel="noreferrer">DevTools UI elements: panels and sidebar panes</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#solutions" target="_blank" rel="noreferrer">Communicating between extension components</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#injecting" target="_blank" rel="noreferrer">Injecting a content script</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#evaluating-js" target="_blank" rel="noreferrer">Evaluating JavaScript in the inspected window</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#selected-element" target="_blank" rel="noreferrer">Passing the selected element to a content script</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#panel-window" target="_blank" rel="noreferrer">Getting a reference panel&#39;s window</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#content-script-to-devtools" target="_blank" rel="noreferrer">Messaging from content scripts to the DevTools page</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#evaluated-scripts-to-devtools" target="_blank" rel="noreferrer">Messaging from injected scripts to the DevTools page</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#detecting-open-close" target="_blank" rel="noreferrer">Detecting when DevTools opens and closes</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#devtools-extension-examples" target="_blank" rel="noreferrer">DevTools extension examples</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#more" target="_blank" rel="noreferrer">More information</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#examples" target="_blank" rel="noreferrer">Examples</a></li></ul><h1 id="extending-devtools" tabindex="-1">Extending DevTools <a class="header-anchor" href="#extending-devtools" aria-hidden="true">#</a></h1><p>Published on Monday, September 17, 2012 \u2022 Updated on Tuesday, June 7, 2022</p><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#overview" target="_blank" rel="noreferrer">Overview</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#devtools-page" target="_blank" rel="noreferrer">The DevTools page</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#creating" target="_blank" rel="noreferrer">Creating a DevTools extension</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#devtools-ui" target="_blank" rel="noreferrer">DevTools UI elements: panels and sidebar panes</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#solutions" target="_blank" rel="noreferrer">Communicating between extension components</a><ul><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#injecting" target="_blank" rel="noreferrer">Injecting a content script</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#evaluating-js" target="_blank" rel="noreferrer">Evaluating JavaScript in the inspected window</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#selected-element" target="_blank" rel="noreferrer">Passing the selected element to a content script</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#panel-window" target="_blank" rel="noreferrer">Getting a reference panel&#39;s window</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#content-script-to-devtools" target="_blank" rel="noreferrer">Messaging from content scripts to the DevTools page</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#evaluated-scripts-to-devtools" target="_blank" rel="noreferrer">Messaging from injected scripts to the DevTools page</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#detecting-open-close" target="_blank" rel="noreferrer">Detecting when DevTools opens and closes</a></li></ul></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#devtools-extension-examples" target="_blank" rel="noreferrer">DevTools extension examples</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#more" target="_blank" rel="noreferrer">More information</a></li><li><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#examples" target="_blank" rel="noreferrer">Examples</a></li></ul><p>Caution</p><p>This page was migrated directly from the Manifest V2 documentation set. It has not yet been validated for compliance with Manifest V3.</p><h2 id="overview" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#overview" target="_blank" rel="noreferrer">#</a> Overview <a class="header-anchor" href="#overview" aria-hidden="true">#</a></h2><p>A DevTools extension adds functionality to the Chrome DevTools. It can add new UI panels and sidebars, interact with the inspected page, get information about network requests, and more. DevTools extensions have access to an additional set of DevTools-specific extension APIs:</p><ul><li><a href="https://developer.chrome.com/docs/extensions/reference/devtools_inspectedWindow/" target="_blank" rel="noreferrer"><code>devtools.inspectedWindow</code></a></li><li><a href="https://developer.chrome.com/docs/extensions/reference/devtools_network/" target="_blank" rel="noreferrer"><code>devtools.network</code></a></li><li><a href="https://developer.chrome.com/docs/extensions/reference/devtools_panels/" target="_blank" rel="noreferrer"><code>devtools.panels</code></a></li><li><a href="https://developer.chrome.com/docs/extensions/reference/devtools_recorder/" target="_blank" rel="noreferrer"><code>devtools.recorder</code> (preview feature)</a></li></ul><p>A DevTools extension is structured like any other extension: it can have a background page, content scripts, and other items. In addition, each DevTools extension has a DevTools page, which has access to the DevTools APIs.</p><p>![Architecture diagram showing DevTools page communicating with the inspected window and the background page. The background page is shown communicating with the content scripts and accessing extension APIs. The DevTools page has access to the DevTools APIs, for example, creating panels.](./Extending DevTools - Chrome Developers_files/kcLMpTY6qtez03TVSqt4.png)</p><h2 id="the-devtools-page" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#devtools-page" target="_blank" rel="noreferrer">#</a> The DevTools page <a class="header-anchor" href="#the-devtools-page" aria-hidden="true">#</a></h2><p>An instance of the extension&#39;s DevTools page is created each time a DevTools window opens. The DevTools page exists for the lifetime of the DevTools window. The DevTools page has access to the DevTools APIs and a limited set of extension APIs. Specifically, the DevTools page can:</p><ul><li>Create and interact with panels using the <a href="https://developer.chrome.com/docs/extensions/reference/devtools_panels/" target="_blank" rel="noreferrer"><code>devtools.panels</code></a> APIs.</li><li>Get information about the inspected window and evaluate code in the inspected window using the <a href="https://developer.chrome.com/docs/extensions/reference/devtools_inspectedWindow/" target="_blank" rel="noreferrer"><code>devtools.inspectedWindow</code></a> APIs.</li><li>Get information about network requests using the <a href="https://developer.chrome.com/docs/extensions/reference/devtools_network/" target="_blank" rel="noreferrer"><code>devtools.network</code></a> APIs.</li><li>Extend the <a href="https://developer.chrome.com/docs/devtools/recorder/" target="_blank" rel="noreferrer">Recorder panel</a> using the <a href="https://developer.chrome.com/docs/extensions/reference/devtools_recorder/" target="_blank" rel="noreferrer"><code>devtools.recorder</code></a> APIs (preview feature).</li></ul><p>The DevTools page cannot use most of the extensions APIs directly. It has access to the same subset of the <a href="https://developer.chrome.com/docs/extensions/reference/extension/" target="_blank" rel="noreferrer"><code>extension</code></a> and <a href="https://developer.chrome.com/docs/extensions/reference/runtime/" target="_blank" rel="noreferrer"><code>runtime</code></a> APIs that a content script has access to. Like a content script, a DevTools page can communicate with the background page using <a href="https://developer.chrome.com/docs/extensions/mv3/messaging/" target="_blank" rel="noreferrer">Message Passing</a>. For an example, see <a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#injecting" target="_blank" rel="noreferrer">Injecting a Content Script</a>.</p><h2 id="creating-a-devtools-extension" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#creating" target="_blank" rel="noreferrer">#</a> Creating a DevTools extension <a class="header-anchor" href="#creating-a-devtools-extension" aria-hidden="true">#</a></h2><p>To create a DevTools page for your extension, add the <code>devtools_page</code> field in the extension manifest:</p><pre><code>{  &quot;name&quot;: ...  &quot;version&quot;: &quot;1.0&quot;,  &quot;minimum_chrome_version&quot;: &quot;10.0&quot;,  &quot;devtools_page&quot;: &quot;devtools.html&quot;,  ...}
</code></pre><p>An instance of the <code>devtools_page</code> specified in your extension&#39;s manifest is created for every DevTools window opened. The page may add other extension pages as panels and sidebars to the DevTools window using the <a href="https://developer.chrome.com/docs/extensions/reference/devtools_panels/" target="_blank" rel="noreferrer"><code>devtools.panels</code></a> API.</p><p>The <code>devtools_page</code> field must point to an HTML page. This differs from the <code>background</code> field, used for specifying a background page, which lets you specify JavaScript files directly. The DevTools page must be local to your extension, so it is best to specify it using a relative URL.</p><p>The <code>chrome.devtools.*</code> API modules are available only to the pages loaded within the DevTools window. Content scripts and other extension pages do not have these APIs. Thus, the APIs are available only through the lifetime of the DevTools window.</p><p>There are also some DevTools APIs that are still experimental. Refer to <a href="https://developer.chrome.com/docs/extensions/reference/" target="_blank" rel="noreferrer">chrome.experimental.* APIs</a> for the list of experimental APIs and guidelines on how to use them.</p><h2 id="devtools-ui-elements-panels-and-sidebar-panes" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#devtools-ui" target="_blank" rel="noreferrer">#</a> DevTools UI elements: panels and sidebar panes <a class="header-anchor" href="#devtools-ui-elements-panels-and-sidebar-panes" aria-hidden="true">#</a></h2><p>In addition to the usual extension UI elements, such as browser actions, context menus and popups, a DevTools extension can add UI elements to the DevTools window:</p><ul><li>A <em>panel</em> is a top-level tab, like the Elements, Sources, and Network panels.</li><li>A <em>sidebar pane</em> presents supplementary UI related to a panel. The Styles, Computed Styles, and Event Listeners panes on the Elements panel are examples of sidebar panes. (Note that the appearance of sidebar panes may not match the image, depending on the version of Chrome you&#39;re using, and where the DevTools window is docked.)</li></ul><p>![DevTools window showing Elements panel and Styles sidebar pane.](./Extending DevTools - Chrome Developers_files/TDNgfhI9byR4eeGQ0Xxv.png)</p><p>Each panel is its own HTML file, which can include other resources (JavaScript, CSS, images, and so on). Creating a basic panel looks like this:</p><pre><code>chrome.devtools.panels.create(&quot;My Panel&quot;,    &quot;MyPanelIcon.png&quot;,    &quot;Panel.html&quot;,    function(panel) {      // code invoked on panel creation    });
</code></pre><p>JavaScript executed in a panel or sidebar pane has access to the the same APIs as the DevTools page.</p><p>Creating a basic sidebar pane for the Elements panel looks like this:</p><pre><code>chrome.devtools.panels.elements.createSidebarPane(&quot;My Sidebar&quot;,    function(sidebar) {        // sidebar initialization code here        sidebar.setObject({ some_data: &quot;Some data to show&quot; });});
</code></pre><p>There are several ways to display content in a sidebar pane:</p><ul><li>HTML content. Call <a href="https://developer.chrome.com/docs/extensions/reference/devtools_panels#method-ExtensionSidebarPane-setPage" target="_blank" rel="noreferrer"><code>setPage</code></a> to specify an HTML page to display in the pane.</li><li>JSON data. Pass a JSON object to <a href="https://developer.chrome.com/docs/extensions/reference/devtools_panels#method-ExtensionSidebarPane-setObject" target="_blank" rel="noreferrer"><code>setObject</code></a>.</li><li>JavaScript expression. Pass an expression to <a href="https://developer.chrome.com/docs/extensions/reference/devtools_panels#method-ExtensionSidebarPane-setExpression" target="_blank" rel="noreferrer"><code>setExpression</code></a>. DevTools evaluates the expression in the context of the inspected page, and displays the return value.</li></ul><p>For both <code>setObject</code> and <code>setExpression</code>, the pane displays the value as it would appear in the DevTools console. However, <code>setExpression</code> lets you display DOM elements and arbitrary JavaScript objects, while <code>setObject</code> only supports JSON objects.</p><h2 id="communicating-between-extension-components" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#solutions" target="_blank" rel="noreferrer">#</a> Communicating between extension components <a class="header-anchor" href="#communicating-between-extension-components" aria-hidden="true">#</a></h2><p>The following sections describe some typical scenarios for communicating between the different components of a DevTools extension.</p><h3 id="injecting-a-content-script" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#injecting" target="_blank" rel="noreferrer">#</a> Injecting a content script <a class="header-anchor" href="#injecting-a-content-script" aria-hidden="true">#</a></h3><p>The DevTools page can&#39;t call <a href="https://developer.chrome.com/docs/extensions/reference/scripting#method-executeScript" target="_blank" rel="noreferrer"><code>scripting.executeScript()</code></a> directly. To inject a content script from the DevTools page, you must retrieve the ID of the inspected window&#39;s tab using the <a href="https://developer.chrome.com/docs/extensions/reference/devtools_inspectedWindow#property-tabId" target="_blank" rel="noreferrer"><code>inspectedWindow.tabId</code></a> property and send a message to the background page. From the background page, call <a href="https://developer.chrome.com/docs/extensions/reference/scripting#method-executeScript" target="_blank" rel="noreferrer"><code>scripting.executeScript</code></a> to inject the script.</p><p>If a content script has already been injected, you can add additional context scripts using the <code>eval()</code> method. See <a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#selected-element" target="_blank" rel="noreferrer">Passing the Selected Element to a Content Script</a> for more information.</p><p>The following code snippets show how to inject a content script using <code>executeScript</code>.</p><pre><code>// DevTools page -- devtools.js// Create a connection to the background pagevar backgroundPageConnection = chrome.runtime.connect({    name: &quot;devtools-page&quot;});backgroundPageConnection.onMessage.addListener(function (message) {    // Handle responses from the background page, if any});// Relay the tab ID to the background pagebackgroundPageConnection.postMessage({    tabId: chrome.devtools.inspectedWindow.tabId,    scriptToInject: &quot;content_script.js&quot;});
</code></pre><p>Code for the background page:</p><pre><code>// Background page -- background.jschrome.runtime.onConnect.addListener(function(devToolsConnection) {    // assign the listener function to a variable so we can remove it later    var devToolsListener = function(message, sender, sendResponse) {        // Inject a content script into the identified tab        chrome.scripting.executeScript(message.tabId,            { file: message.scriptToInject });    }    // add the listener    devToolsConnection.onMessage.addListener(devToolsListener);    devToolsConnection.onDisconnect.addListener(function() {         devToolsConnection.onMessage.removeListener(devToolsListener);    });});
</code></pre><h3 id="evaluating-javascript-in-the-inspected-window" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#evaluating-js" target="_blank" rel="noreferrer">#</a> Evaluating JavaScript in the inspected window <a class="header-anchor" href="#evaluating-javascript-in-the-inspected-window" aria-hidden="true">#</a></h3><p>You can use the <a href="https://developer.chrome.com/docs/extensions/reference/devtools_inspectedWindow#method-eval" target="_blank" rel="noreferrer"><code>inspectedWindow.eval()</code></a> method to execute JavaScript code in the context of the inspected page. You can invoke the <code>eval()</code> method from a DevTools page, panel or sidebar pane.</p><p>By default, the expression is evaluated in the context of the main frame of the page. Now, you may be familiar with the DevTools <a href="https://developer.chrome.com/docs/devtools/console/utilities/" target="_blank" rel="noreferrer">Console Utilities API</a> features like element inspection (<code>inspect(elem)</code>), breaking on functions (<code>debug(fn)</code>), copying to clipboard (<code>copy()</code>) and more. <code>inspectedWindow.eval()</code> uses the same script execution context and options as the code typed at the DevTools console, which allows access to these APIs within the eval. For example, <a href="https://github.com/RedRibbon/SOAK/blob/ffdfad68ffb6051fa2d4e9db0219b3d234ac1ae8/pages/devtools.js#L6-L8" target="_blank" rel="noreferrer">SOAK</a> uses it for inspecting an element:</p><pre><code>chrome.devtools.inspectedWindow.eval(  &quot;inspect($$(&#39;head script[data-soak=main]&#39;)[0])&quot;,  function(result, isException) { });
</code></pre><p>Alternatively, use the <code>useContentScriptContext: true</code> option for <code>inspectedWindow.eval()</code> to evaluate the expression in the same context as the content scripts. Calling <code>eval</code> with <code>useContentScriptContext: true</code> does not <em>create</em> a content script context, so you must load a context script before calling <code>eval</code>, either by calling <code>executeScript</code> or by specifying a content script in the <code>manifest.json</code> file.</p><p>Once the context script context exists, you can use this option to inject additional content scripts.</p><p>The <code>eval</code> method is powerful when used in the right context and dangerous when used inappropriately. Use the <a href="https://developer.chrome.com/docs/extensions/reference/scripting#method-executeScript" target="_blank" rel="noreferrer"><code>scripting.executeScript()</code></a> method if you don&#39;t need access to the JavaScript context of the inspected page. For detailed cautions and a comparison of the two methods, see <a href="https://developer.chrome.com/docs/extensions/reference/devtools_inspectedWindow/" target="_blank" rel="noreferrer"><code>inspectedWindow</code></a>.</p><h3 id="passing-the-selected-element-to-a-content-script" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#selected-element" target="_blank" rel="noreferrer">#</a> Passing the selected element to a content script <a class="header-anchor" href="#passing-the-selected-element-to-a-content-script" aria-hidden="true">#</a></h3><p>The content script doesn&#39;t have direct access to the current selected element. However, any code you execute using <a href="https://developer.chrome.com/docs/extensions/reference/devtools_inspectedWindow#method-eval" target="_blank" rel="noreferrer"><code>inspectedWindow.eval()</code></a> has access to the DevTools console and Console Utilities APIs. For example, in evaluated code you can use <code>$0</code> to access the selected element.</p><p>To pass the selected element to a content script:</p><ul><li>Create a method in the content script that takes the selected element as an argument.</li><li>Call the method from the DevTools page using <a href="https://developer.chrome.com/docs/extensions/reference/devtools_inspectedWindow#method-eval" target="_blank" rel="noreferrer"><code>inspectedWindow.eval()</code></a> with the <code>useContentScriptContext: true</code> option.</li></ul><p>The code in your content script might look something like this:</p><pre><code>function setSelectedElement(el) {    // do something with the selected element}
</code></pre><p>Invoke the method from the DevTools page like this:</p><pre><code>chrome.devtools.inspectedWindow.eval(&quot;setSelectedElement($0)&quot;,    { useContentScriptContext: true });
</code></pre><p>The <code>useContentScriptContext: true</code> option specifies that the expression must be evaluated in the same context as the content scripts, so it can access the <code>setSelectedElement</code> method.</p><h3 id="getting-a-reference-panel-s-window" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#panel-window" target="_blank" rel="noreferrer">#</a> Getting a reference panel&#39;s <code>window</code> <a class="header-anchor" href="#getting-a-reference-panel-s-window" aria-hidden="true">#</a></h3><p>To call <code>postMessage()</code> from a devtools panel, you&#39;ll need a reference to its <code>window</code> object. Get a panel&#39;s iframe window in from the the <a href="https://developer.chrome.com/docs/extensions/reference/devtools_panels#event-ExtensionPanel-onShown" target="_blank" rel="noreferrer"><code>panel.onShown</code></a> event handler:</p><pre><code>onShown.addListener(function callback)extensionPanel.onShown.addListener(function (extPanelWindow) {    extPanelWindow instanceof Window; // true    extPanelWindow.postMessage( // \u2026});
</code></pre><h3 id="messaging-from-content-scripts-to-the-devtools-page" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#content-script-to-devtools" target="_blank" rel="noreferrer">#</a> Messaging from content scripts to the DevTools page <a class="header-anchor" href="#messaging-from-content-scripts-to-the-devtools-page" aria-hidden="true">#</a></h3><p>Messaging between the DevTools page and content scripts is indirect, by way of the background page.</p><p>When sending a message <em>to</em> a content script, the background page can use the <a href="https://developer.chrome.com/docs/extensions/reference/scripting#method-sendMessage" target="_blank" rel="noreferrer"><code>tabs.sendMessage()</code></a> method, which directs a message to the content scripts in a specific tab, as shown in <a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#injecting" target="_blank" rel="noreferrer">Injecting a Content Script</a>.</p><p>When sending a message <em>from</em> a content script, there is no ready-made method to deliver a message to the correct DevTools page instance associated with the current tab. As a workaround, you can have the DevTools page establish a long-lived connection with the background page, and have the background page keep a map of tab IDs to connections, so it can route each message to the correct connection.</p><pre><code>// background.jsvar connections = {};chrome.runtime.onConnect.addListener(function (port) {    var extensionListener = function (message, sender, sendResponse) {        // The original connection event doesn&#39;t include the tab ID of the        // DevTools page, so we need to send it explicitly.        if (message.name == &quot;init&quot;) {          connections[message.tabId] = port;          return;        }	// other message handling    }    // Listen to messages sent from the DevTools page    port.onMessage.addListener(extensionListener);    port.onDisconnect.addListener(function(port) {        port.onMessage.removeListener(extensionListener);        var tabs = Object.keys(connections);        for (var i=0, len=tabs.length; i &lt; len; i++) {          if (connections[tabs[i]] == port) {            delete connections[tabs[i]]            break;          }        }    });});// Receive message from content script and relay to the devTools page for the// current tabchrome.runtime.onMessage.addListener(function(request, sender, sendResponse) {    // Messages from content scripts should have sender.tab set    if (sender.tab) {      var tabId = sender.tab.id;      if (tabId in connections) {        connections[tabId].postMessage(request);      } else {        console.log(&quot;Tab not found in connection list.&quot;);      }    } else {      console.log(&quot;sender.tab not defined.&quot;);    }    return true;});
</code></pre><p>The DevTools page (or panel or sidebar pane) establishes the connection like this:</p><pre><code>// Create a connection to the background pagevar backgroundPageConnection = chrome.runtime.connect({    name: &quot;panel&quot;});backgroundPageConnection.postMessage({    name: &#39;init&#39;,    tabId: chrome.devtools.inspectedWindow.tabId});
</code></pre><h3 id="messaging-from-injected-scripts-to-the-devtools-page" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#evaluated-scripts-to-devtools" target="_blank" rel="noreferrer">#</a> Messaging from injected scripts to the DevTools page <a class="header-anchor" href="#messaging-from-injected-scripts-to-the-devtools-page" aria-hidden="true">#</a></h3><p>While the above solution works for content scripts, code that is injected directly into the page (e.g. through appending a <code>&lt;script&gt;</code> tag or through <a href="https://developer.chrome.com/docs/extensions/reference/devtools_inspectedWindow#method-eval" target="_blank" rel="noreferrer"><code>inspectedWindow.eval()</code></a>) requires a different strategy. In this context, <a href="https://developer.chrome.com/docs/extensions/reference/runtime#method-sendMessage" target="_blank" rel="noreferrer"><code>runtime.sendMessage()</code></a> will not pass messages to the background script as expected.</p><p>As a workaround, you can combine your injected script with a content script that acts as an intermediary. To pass messages to the content script, you can use the <a href="https://developer.mozilla.org/docs/Web/API/Window.postMessage" target="_blank" rel="noreferrer"><code>window.postMessage()</code></a> method. Here&#39;s an example, assuming the background script from the previous section:</p><pre><code>// injected-script.jswindow.postMessage({  greeting: &#39;hello there!&#39;,  source: &#39;my-devtools-extension&#39;}, &#39;*&#39;);

// content-script.jswindow.addEventListener(&#39;message&#39;, function(event) {  // Only accept messages from the same frame  if (event.source !== window) {    return;  }  var message = event.data;  // Only accept messages that we know are ours  if (typeof message !== &#39;object&#39; || message === null ||      message.source !== &#39;my-devtools-extension&#39;) {    return;  }  chrome.runtime.sendMessage(message);});
</code></pre><p>Your message will now flow from the injected script, to the content script, to the background script, and finally to the DevTools page.</p><p>You can also consider <a href="https://github.com/GoogleChrome/devtools-docs/issues/143" target="_blank" rel="noreferrer">two alternative message passing techniques outlined here</a>.</p><h3 id="detecting-when-devtools-opens-and-closes" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#detecting-open-close" target="_blank" rel="noreferrer">#</a> Detecting when DevTools opens and closes <a class="header-anchor" href="#detecting-when-devtools-opens-and-closes" aria-hidden="true">#</a></h3><p>If your extension needs to track whether the DevTools window is open, you can add an <a href="https://developer.chrome.com/docs/extensions/reference/runtime#event-onConnect" target="_blank" rel="noreferrer">onConnect</a> listener to the background page, and call <a href="https://developer.chrome.com/docs/extensions/reference/runtime#method-connect" target="_blank" rel="noreferrer">connect()</a> from the DevTools page. Since each tab can have its own DevTools window open, you may receive multiple connect events. To track whether any DevTools window is open, you need to count the connect and disconnect events as shown below:</p><pre><code>// background.jsvar openCount = 0;chrome.runtime.onConnect.addListener(function (port) {    if (port.name == &quot;devtools-page&quot;) {      if (openCount == 0) {        alert(&quot;DevTools window opening.&quot;);      }      openCount++;      port.onDisconnect.addListener(function(port) {          openCount--;          if (openCount == 0) {            alert(&quot;Last DevTools window closing.&quot;);          }      });    }});
</code></pre><p>The DevTools page creates a connection like this:</p><pre><code>// devtools.js// Create a connection to the background pagevar backgroundPageConnection = chrome.runtime.connect({    name: &quot;devtools-page&quot;});
</code></pre><h2 id="devtools-extension-examples" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#devtools-extension-examples" target="_blank" rel="noreferrer">#</a> DevTools extension examples <a class="header-anchor" href="#devtools-extension-examples" aria-hidden="true">#</a></h2><p>Browse the source of these DevTools extension examples:</p><ul><li><a href="https://github.com/PolymerLabs/polymer-devtools-extension" target="_blank" rel="noreferrer">Polymer Devtools Extension</a> - Uses many helpers running in the host page to query DOM/JS state to send back to the custom panel.</li><li><a href="https://github.com/facebook/react/tree/main/scripts/devtools" target="_blank" rel="noreferrer">React DevTools Extension</a> - Uses a submodule of the renderer to reuse DevTools UI components.</li><li><a href="https://github.com/emberjs/ember-inspector" target="_blank" rel="noreferrer">Ember Inspector</a> - Shared extension core with adapters for both Chrome and Firefox.</li><li><a href="https://github.com/thomasboyt/coquette-inspect" target="_blank" rel="noreferrer">Coquette-inspect</a> - A clean React-based extension with a debugging agent injected into the host page.</li><li><a href="https://developer.chrome.com/docs/extensions/mv3/samples#devtools" target="_blank" rel="noreferrer">Sample Extensions</a> have more worthwhile extensions to install, try out, and learn from.</li></ul><h2 id="more-information" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#more" target="_blank" rel="noreferrer">#</a> More information <a class="header-anchor" href="#more-information" aria-hidden="true">#</a></h2><p>For information on the standard APIs that extensions can use, see <a href="https://developer.chrome.com/docs/extensions/reference/" target="_blank" rel="noreferrer">chrome.* APIs</a> and <a href="https://developer.mozilla.org/docs/Web/API" target="_blank" rel="noreferrer">web APIs</a>.</p><p><a href="https://groups.google.com/g/google-chrome-developer-tools" target="_blank" rel="noreferrer">Give us feedback!</a> Your comments and suggestions help us improve the APIs.</p><h2 id="examples" tabindex="-1"><a href="https://developer.chrome.com/docs/extensions/mv3/devtools/#examples" target="_blank" rel="noreferrer">#</a> Examples <a class="header-anchor" href="#examples" aria-hidden="true">#</a></h2><p>You can find examples that use DevTools APIs in <a href="https://developer.chrome.com/docs/extensions/mv3/samples#devtools" target="_blank" rel="noreferrer">Samples</a>.</p><p>Updated on Tuesday, June 7, 2022 \u2022 <a href="https://github.com/GoogleChrome/developer.chrome.com/blob/main/site/en/docs/extensions/mv3/devtools/index.md" target="_blank" rel="noreferrer">Improve article</a></p>`,89),a=[r];function i(c,l,d,p,h,m){return o(),t("div",null,a)}const f=e(s,[["render",i]]);export{g as __pageData,f as default};
